        private bool _blnIsLoading;

        /// <summary>
        /// Set to true while data is being populated by the Load function
        /// </summary>
        public bool IsLoading
        {
            get
            {
                using (EnterReadLock.Enter(LockObject))
                    return _blnIsLoading;
            }
            set
            {
                using (EnterReadLock.Enter(LockObject))
                {
                    if (_blnIsLoading == value)
                        return;
                    using (EnterReadLock.Enter(LockObject))
                    {
                        _blnIsLoading = value;
                        OnPropertyChanged();
                    }
                }
            }
        }

