        /// <summary>
        /// Remove stray event handlers and clear all info used by this character
        /// </summary>
        public void Dispose()
        {
            if (LockObject.IsDisposed)
                return;

            if (Program.OpenCharacters.Contains(this)
                || Program.OpenCharacters.Any(x => x.LinkedCharacters.Contains(this)))
