                            else if (objArmorMod != null)
                                strGearReturn += strSpace + '(' + objArmor.DisplayNameShort(strLanguage) + ','
                                                 + strSpace
                                                 + objArmorMod.DisplayNameShort(strLanguage) + ')';
                            else
                                strGearReturn += strSpace + '(' + objArmor.DisplayNameShort(strLanguage) + ')';

                            if (blnWireless)
