                using (EnterReadLock.Enter(LockObject))
                {
                    int intLimit = (LOG.TotalValue * 2 + INT.TotalValue + WIL.TotalValue + 2) / 3;
                    if (IsAI && HomeNode != null)
                    {
                        if (HomeNode is Vehicle objHomeNodeVehicle)
