            if (objXmlSensorGear?.InnerXml.Contains("<addoncategory>") == true)
            {
                foreach (XmlNode objXmlCategory in objXmlSensorGear.SelectNodes("addoncategory"))
                    strCategories += objXmlCategory.InnerText + ",";
