                // If this isn't an Accessory, then it must be a Vehicle Mod.
                VehicleMod objVehicleMod = CommonFunctions.FindVehicleMod(treVehicles.SelectedNode.Tag.ToString(), CharacterObject.Vehicles);
                if (objVehicleMod != null)
