        internal async ValueTask ReapplySpecificImprovements(string strSelectedId, string strSelectedName,
                                                            CancellationToken token = default)
        {
            token.ThrowIfCancellationRequested();
            if (string.IsNullOrEmpty(strSelectedId))
                return;
            string strName = !string.IsNullOrEmpty(strSelectedName)
                ? strSelectedName
                : await LanguageManager.GetStringAsync("String_SelectedItem", token: token).ConfigureAwait(false);
            // This only re-applies the Improvements for everything the character has. If a match is not found in the data files, the current Improvement information is left as-is.
            // Verify that the user wants to go through with it.
            if (Program.ShowMessageBox(
                    this,
                    string.Format(GlobalSettings.CultureInfo,
                                  await LanguageManager
                                        .GetStringAsync("Message_ConfirmReapplySpecificImprovements", token: token)
                                        .ConfigureAwait(false), strName),
                    await LanguageManager
                          .GetStringAsync("MessageTitle_ConfirmReapplySpecificImprovements", token: token)
                          .ConfigureAwait(false),
                    MessageBoxButtons.YesNo, MessageBoxIcon.Question) == DialogResult.No)
                return;
            await DoReapplyImprovements(strSelectedId.Yield().ToList(), token: token).ConfigureAwait(false);
        }

        private async ValueTask DoReapplyImprovements(IReadOnlyCollection<string> lstInternalIdFilter = null,
                                                      CancellationToken token = default)
        {
            token.ThrowIfCancellationRequested();
            CursorWait objCursorWait = await CursorWait.NewAsync(this, token: token).ConfigureAwait(false);
            try
            {
                IAsyncDisposable objLocker
                    = await CharacterObject.LockObject.EnterWriteLockAsync(token).ConfigureAwait(false);
                try
                {
                    token.ThrowIfCancellationRequested();
                    using (new FetchSafelyFromPool<StringBuilder>(Utils.StringBuilderPool,
                                                                  out StringBuilder sbdOutdatedItems))
                    {
                        // Record the status of any flags that normally trigger character events.
                        bool blnMAGEnabled = await CharacterObject.GetMAGEnabledAsync(token).ConfigureAwait(false);
                        bool blnRESEnabled = await CharacterObject.GetRESEnabledAsync(token).ConfigureAwait(false);
                        bool blnDEPEnabled = await CharacterObject.GetDEPEnabledAsync(token).ConfigureAwait(false);
                        decimal decEssenceAtSpecialStart = CharacterObject.EssenceAtSpecialStart;

                        _blnReapplyImprovements = true;

                        // Wipe all improvements that we will reapply, this is mainly to eliminate orphaned improvements caused by certain bugs and also for a performance increase
                        if (lstInternalIdFilter == null)
                            await ImprovementManager.RemoveImprovementsAsync(
                                CharacterObject,
                                await (await CharacterObject.GetImprovementsAsync(token).ConfigureAwait(false))
                                      .ToListAsync(
                                          x => x.ImproveSource == Improvement.ImprovementSource.AIProgram
                                               || x.ImproveSource == Improvement.ImprovementSource.Armor
                                               || x.ImproveSource == Improvement.ImprovementSource.ArmorMod
                                               || x.ImproveSource == Improvement.ImprovementSource.Bioware
                                               || x.ImproveSource == Improvement.ImprovementSource.ComplexForm
                                               || x.ImproveSource == Improvement.ImprovementSource.CritterPower
                                               || x.ImproveSource == Improvement.ImprovementSource.Cyberware
                                               || x.ImproveSource == Improvement.ImprovementSource.Echo
                                               || x.ImproveSource == Improvement.ImprovementSource.Gear
                                               || x.ImproveSource == Improvement.ImprovementSource.MartialArt
                                               || x.ImproveSource == Improvement.ImprovementSource.MartialArtTechnique
                                               || x.ImproveSource == Improvement.ImprovementSource.Metamagic
                                               || x.ImproveSource == Improvement.ImprovementSource.Power
                                               || x.ImproveSource == Improvement.ImprovementSource.Quality
                                               || x.ImproveSource == Improvement.ImprovementSource.Spell
                                               || x.ImproveSource == Improvement.ImprovementSource.StackedFocus, token)
                                      .ConfigureAwait(false),
                                _blnReapplyImprovements, token: token).ConfigureAwait(false);
                        else
                            await ImprovementManager.RemoveImprovementsAsync(
                                CharacterObject,
                                await (await CharacterObject.GetImprovementsAsync(token).ConfigureAwait(false))
                                      .ToListAsync(
                                          x => lstInternalIdFilter.Contains(x.SourceName) &&
                                               (x.ImproveSource == Improvement.ImprovementSource.AIProgram
                                                || x.ImproveSource == Improvement.ImprovementSource.Armor
                                                || x.ImproveSource == Improvement.ImprovementSource.ArmorMod
                                                || x.ImproveSource == Improvement.ImprovementSource.Bioware
                                                || x.ImproveSource == Improvement.ImprovementSource.ComplexForm
                                                || x.ImproveSource == Improvement.ImprovementSource.CritterPower
                                                || x.ImproveSource == Improvement.ImprovementSource.Cyberware
                                                || x.ImproveSource == Improvement.ImprovementSource.Echo
                                                || x.ImproveSource == Improvement.ImprovementSource.Gear
                                                || x.ImproveSource == Improvement.ImprovementSource.MartialArt
                                                || x.ImproveSource == Improvement.ImprovementSource.MartialArtTechnique
                                                || x.ImproveSource == Improvement.ImprovementSource.Metamagic
                                                || x.ImproveSource == Improvement.ImprovementSource.Power
                                                || x.ImproveSource == Improvement.ImprovementSource.Quality
                                                || x.ImproveSource == Improvement.ImprovementSource.Spell
                                                || x.ImproveSource == Improvement.ImprovementSource.StackedFocus),
                                          token).ConfigureAwait(false),
                                _blnReapplyImprovements, token: token).ConfigureAwait(false);

                        // Refresh Qualities.
                        // We cannot use foreach because qualities can add more qualities
                        for (int j = 0; j < CharacterObject.Qualities.Count; j++)
                        {
                            Quality objQuality = CharacterObject.Qualities[j];
                            if (objQuality.OriginSource == QualitySource.Improvement
                                || objQuality.OriginSource == QualitySource.MetatypeRemovedAtChargen)
                                continue;
                            // We're only re-apply improvements a list of items, not all of them
                            if (lstInternalIdFilter?.Contains(objQuality.InternalId) == false)
                                continue;

                            XmlNode objNode = await objQuality.GetNodeAsync(token).ConfigureAwait(false);
                            if (objNode != null)
                            {
                                string strSelected = objQuality.Extra;
                                objQuality.Bonus = objNode["bonus"];
                                if (objQuality.Bonus != null)
                                {
                                    ImprovementManager.ForcedValue = strSelected;
                                    await ImprovementManager.CreateImprovementsAsync(
                                        CharacterObject, Improvement.ImprovementSource.Quality, objQuality.InternalId,
                                        objQuality.Bonus, 1,
                                        await objQuality.GetCurrentDisplayNameShortAsync(token).ConfigureAwait(false),
                                        token: token).ConfigureAwait(false);
                                    if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue))
                                    {
                                        objQuality.Extra = ImprovementManager.SelectedValue;
                                        string strName = await objQuality.GetCurrentDisplayNameAsync(token)
                                                                         .ConfigureAwait(false);
                                        await treQualities.DoThreadSafeAsync(x =>
                                        {
                                            TreeNode objTreeNode = x.FindNodeByTag(objQuality);
                                            if (objTreeNode != null)
                                                objTreeNode.Text = strName;
                                        }, token).ConfigureAwait(false);
                                    }
                                }

                                objQuality.FirstLevelBonus = objNode["firstlevelbonus"];
                                if (objQuality.FirstLevelBonus?.HasChildNodes == true)
                                {
                                    bool blnDoFirstLevel = true;
                                    for (int k = 0; k < CharacterObject.Qualities.Count; ++k)
                                    {
                                        Quality objCheckQuality = CharacterObject.Qualities[k];
                                        if (j != k
                                            && objCheckQuality.SourceIDString == objQuality.SourceIDString
                                            && objCheckQuality.Extra == objQuality.Extra
                                            && objCheckQuality.SourceName == objQuality.SourceName
                                            && (k < j
                                                || objCheckQuality.OriginSource == QualitySource.Improvement
                                                || (lstInternalIdFilter?.Contains(objCheckQuality.InternalId)
                                                    == false)))
                                        {
                                            blnDoFirstLevel = false;
                                            break;
                                        }
                                    }

                                    if (blnDoFirstLevel)
                                    {
                                        ImprovementManager.ForcedValue = strSelected;
                                        await ImprovementManager.CreateImprovementsAsync(
                                            CharacterObject, Improvement.ImprovementSource.Quality,
                                            objQuality.InternalId, objQuality.FirstLevelBonus, 1,
                                            await objQuality.GetCurrentDisplayNameShortAsync(token)
                                                            .ConfigureAwait(false), token: token).ConfigureAwait(false);
                                        if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue))
                                        {
                                            objQuality.Extra = ImprovementManager.SelectedValue;
                                            string strName = await objQuality.GetCurrentDisplayNameAsync(token)
                                                                             .ConfigureAwait(false);
                                            await treQualities.DoThreadSafeAsync(x =>
                                            {
                                                TreeNode objTreeNode = x.FindNodeByTag(objQuality);
                                                if (objTreeNode != null)
                                                    objTreeNode.Text = strName;
                                            }, token).ConfigureAwait(false);
                                        }
                                    }
                                }

                                objQuality.NaturalWeaponsNode = objNode["naturalweapons"];
                                if (objQuality.NaturalWeaponsNode != null)
                                {
                                    ImprovementManager.ForcedValue = strSelected;
                                    await ImprovementManager.CreateImprovementsAsync(
                                        CharacterObject, Improvement.ImprovementSource.Quality, objQuality.InternalId,
                                        objQuality.NaturalWeaponsNode, 1,
                                        await objQuality.GetCurrentDisplayNameShortAsync(token).ConfigureAwait(false),
                                        token: token).ConfigureAwait(false);
                                    if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue))
                                    {
                                        objQuality.Extra = ImprovementManager.SelectedValue;
                                        string strName = await objQuality.GetCurrentDisplayNameAsync(token)
                                                                         .ConfigureAwait(false);
                                        await treQualities.DoThreadSafeAsync(x =>
                                        {
                                            TreeNode objTreeNode = x.FindNodeByTag(objQuality);
                                            if (objTreeNode != null)
                                                objTreeNode.Text = strName;
                                        }, token).ConfigureAwait(false);
                                    }
                                }
                            }
                            else
                            {
                                sbdOutdatedItems.AppendLine(
                                    await objQuality.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                            }
                        }

                        // Refresh Martial Art Techniques.
                        foreach (MartialArt objMartialArt in CharacterObject.MartialArts)
                        {
                            XmlNode objMartialArtNode = await objMartialArt.GetNodeAsync(token).ConfigureAwait(false);
                            if (objMartialArtNode != null)
                            {
                                // We're only re-apply improvements a list of items, not all of them
                                if (lstInternalIdFilter?.Contains(objMartialArt.InternalId) != false
                                    && objMartialArtNode["bonus"] != null)
                                {
                                    await ImprovementManager.CreateImprovementsAsync(
                                        CharacterObject, Improvement.ImprovementSource.MartialArt,
                                        objMartialArt.InternalId, objMartialArtNode["bonus"], 1,
                                        await objMartialArt.GetCurrentDisplayNameAsync(token).ConfigureAwait(false),
                                        token: token).ConfigureAwait(false);
                                }
                            }
                            else
                            {
                                sbdOutdatedItems.AppendLine(
                                    await objMartialArt.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                            }

                            foreach (MartialArtTechnique objTechnique in objMartialArt.Techniques.Where(
                                         x => lstInternalIdFilter?.Contains(x.InternalId) != false))
                            {
                                XmlNode objNode = await objTechnique.GetNodeAsync(token).ConfigureAwait(false);
                                if (objNode != null)
                                {
                                    if (objNode["bonus"] != null)
                                        await ImprovementManager.CreateImprovementsAsync(
                                            CharacterObject, Improvement.ImprovementSource.MartialArtTechnique,
                                            objTechnique.InternalId, objNode["bonus"], 1,
                                            await objTechnique.GetCurrentDisplayNameAsync(token).ConfigureAwait(false),
                                            token: token).ConfigureAwait(false);
                                }
                                else
                                {
                                    sbdOutdatedItems.AppendLine(
                                        await objTechnique.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                                }
                            }
                        }

                        // Refresh Spells.
                        foreach (Spell objSpell in CharacterObject.Spells.Where(
                                     x => lstInternalIdFilter?.Contains(x.InternalId) != false))
                        {
                            XmlNode objNode = await objSpell.GetNodeAsync(token).ConfigureAwait(false);
                            if (objNode != null)
                            {
                                if (objNode["bonus"] != null)
                                {
                                    ImprovementManager.ForcedValue = objSpell.Extra;
                                    await ImprovementManager.CreateImprovementsAsync(
                                        CharacterObject, Improvement.ImprovementSource.Spell, objSpell.InternalId,
                                        objNode["bonus"], 1,
                                        await objSpell.GetCurrentDisplayNameShortAsync(token).ConfigureAwait(false),
                                        token: token).ConfigureAwait(false);
                                    if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue))
                                    {
                                        objSpell.Extra = ImprovementManager.SelectedValue;
                                        string strName = await objSpell.GetCurrentDisplayNameAsync(token)
                                                                       .ConfigureAwait(false);
                                        await treSpells.DoThreadSafeAsync(x =>
                                        {
                                            TreeNode objSpellNode = x.FindNode(objSpell.InternalId);
                                            if (objSpellNode != null)
                                                objSpellNode.Text = strName;
                                        }, token).ConfigureAwait(false);
                                    }
                                }
                            }
                            else
                            {
                                sbdOutdatedItems.AppendLine(
                                    await objSpell.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                            }
                        }

                        // Refresh Adept Powers.
                        foreach (Power objPower in CharacterObject.Powers.Where(
                                     x => lstInternalIdFilter?.Contains(x.InternalId) != false))
                        {
                            XmlNode objNode = await objPower.GetNodeAsync(token).ConfigureAwait(false);
                            if (objNode != null)
                            {
                                objPower.Bonus = objNode["bonus"];
                                if (objPower.Bonus != null)
                                {
                                    ImprovementManager.ForcedValue = objPower.Extra;
                                    await ImprovementManager.CreateImprovementsAsync(
                                        CharacterObject, Improvement.ImprovementSource.Power, objPower.InternalId,
                                        objPower.Bonus, await objPower.GetTotalRatingAsync(token).ConfigureAwait(false),
                                        await objPower.GetCurrentDisplayNameShortAsync(token).ConfigureAwait(false),
                                        token: token).ConfigureAwait(false);
                                }
                            }
                            else
                            {
                                sbdOutdatedItems.AppendLine(
                                    await objPower.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                            }
                        }

                        // Refresh Complex Forms.
                        foreach (ComplexForm objComplexForm in CharacterObject.ComplexForms.Where(
                                     x => lstInternalIdFilter?.Contains(x.InternalId) != false))
                        {
                            XmlNode objNode = await objComplexForm.GetNodeAsync(token).ConfigureAwait(false);
                            if (objNode != null)
                            {
                                if (objNode["bonus"] != null)
                                {
                                    ImprovementManager.ForcedValue = objComplexForm.Extra;
                                    await ImprovementManager.CreateImprovementsAsync(
                                        CharacterObject, Improvement.ImprovementSource.ComplexForm,
                                        objComplexForm.InternalId, objNode["bonus"], 1,
                                        await objComplexForm.GetCurrentDisplayNameShortAsync(token)
                                                            .ConfigureAwait(false), token: token).ConfigureAwait(false);
                                    if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue))
                                    {
                                        objComplexForm.Extra = ImprovementManager.SelectedValue;
                                        string strName = await objComplexForm.GetCurrentDisplayNameAsync(token)
                                                                             .ConfigureAwait(false);
                                        await treComplexForms.DoThreadSafeAsync(x =>
                                        {
                                            TreeNode objCFNode = x.FindNode(objComplexForm.InternalId);
                                            if (objCFNode != null)
                                                objCFNode.Text = strName;
                                        }, token).ConfigureAwait(false);
                                    }
                                }
                            }
                            else
                            {
                                sbdOutdatedItems.AppendLine(
                                    await objComplexForm.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                            }
                        }

                        // Refresh AI Programs and Advanced Programs
                        foreach (AIProgram objProgram in CharacterObject.AIPrograms.Where(
                                     x => lstInternalIdFilter?.Contains(x.InternalId) != false))
                        {
                            XmlNode objNode = await objProgram.GetNodeAsync(token).ConfigureAwait(false);
                            if (objNode != null)
                            {
                                if (objNode["bonus"] != null)
                                {
                                    ImprovementManager.ForcedValue = objProgram.Extra;
                                    await ImprovementManager.CreateImprovementsAsync(
                                        CharacterObject, Improvement.ImprovementSource.AIProgram, objProgram.InternalId,
                                        objNode["bonus"], 1,
                                        await objProgram.GetCurrentDisplayNameShortAsync(token).ConfigureAwait(false),
                                        token: token).ConfigureAwait(false);
                                    if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue))
                                    {
                                        objProgram.Extra = ImprovementManager.SelectedValue;
                                        string strName = await objProgram.GetCurrentDisplayNameShortAsync(token)
                                                                         .ConfigureAwait(false);
                                        await treAIPrograms.DoThreadSafeAsync(x =>
                                        {
                                            TreeNode objProgramNode = x.FindNode(objProgram.InternalId);
                                            if (objProgramNode != null)
                                                objProgramNode.Text = strName;
                                        }, token).ConfigureAwait(false);
                                    }
                                }
                            }
                            else
                            {
                                sbdOutdatedItems.AppendLine(
                                    await objProgram.GetCurrentDisplayNameShortAsync(token).ConfigureAwait(false));
                            }
                        }

                        // Refresh Critter Powers.
                        foreach (CritterPower objPower in CharacterObject.CritterPowers.Where(
                                     x => lstInternalIdFilter?.Contains(x.InternalId) != false))
                        {
                            XmlNode objNode = await objPower.GetNodeAsync(token).ConfigureAwait(false);
                            if (objNode != null)
                            {
                                objPower.Bonus = objNode["bonus"];
                                if (objPower.Bonus != null)
                                {
                                    string strSelected = objPower.Extra;
                                    if (!int.TryParse(strSelected, out int intRating))
                                    {
                                        intRating = 1;
                                        ImprovementManager.ForcedValue = strSelected;
                                    }

                                    await ImprovementManager.CreateImprovementsAsync(
                                        CharacterObject, Improvement.ImprovementSource.CritterPower,
                                        objPower.InternalId, objPower.Bonus, intRating,
                                        await objPower.GetCurrentDisplayNameShortAsync(token).ConfigureAwait(false),
                                        token: token).ConfigureAwait(false);
                                    if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue))
                                    {
                                        objPower.Extra = ImprovementManager.SelectedValue;
                                        string strName = await objPower.GetCurrentDisplayNameAsync(token)
                                                                       .ConfigureAwait(false);
                                        await treCritterPowers.DoThreadSafeAsync(x =>
                                        {
                                            TreeNode objPowerNode = x.FindNode(objPower.InternalId);
                                            if (objPowerNode != null)
                                                objPowerNode.Text = strName;
                                        }, token).ConfigureAwait(false);
                                    }
                                }
                            }
                            else
                            {
                                sbdOutdatedItems.AppendLine(
                                    await objPower.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                            }
                        }

                        // Refresh Metamagics and Echoes.
                        // We cannot use foreach because metamagics/echoes can add more metamagics/echoes
                        // ReSharper disable once ForCanBeConvertedToForeach
                        for (int j = 0; j < CharacterObject.Metamagics.Count; j++)
                        {
                            Metamagic objMetamagic = CharacterObject.Metamagics[j];
                            if (objMetamagic.Grade < 0)
                                continue;
                            // We're only re-apply improvements a list of items, not all of them
                            if (lstInternalIdFilter?.Contains(objMetamagic.InternalId) == false)
                                continue;
                            XmlNode objNode = await objMetamagic.GetNodeAsync(token).ConfigureAwait(false);
                            if (objNode != null)
                            {
                                objMetamagic.Bonus = objNode["bonus"];
                                if (objMetamagic.Bonus != null)
                                {
                                    await ImprovementManager.CreateImprovementsAsync(
                                        CharacterObject, objMetamagic.SourceType, objMetamagic.InternalId,
                                        objMetamagic.Bonus, 1,
                                        await objMetamagic.GetCurrentDisplayNameShortAsync(token).ConfigureAwait(false),
                                        token: token).ConfigureAwait(false);
                                }
                            }
                            else
                            {
                                sbdOutdatedItems.AppendLine(
                                    await objMetamagic.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                            }
                        }

                        // Refresh Cyberware and Bioware.
                        Dictionary<Cyberware, int> dicPairableCyberwares
                            = new Dictionary<Cyberware, int>(CharacterObject.Cyberware.Count);
                        foreach (Cyberware objCyberware in CharacterObject.Cyberware.GetAllDescendants(x => x.Children))
                        {
                            // We're only re-apply improvements a list of items, not all of them
                            if (lstInternalIdFilter?.Contains(objCyberware.InternalId) != false)
                            {
                                XmlNode objNode = await objCyberware.GetNodeAsync(token).ConfigureAwait(false);
                                if (objNode != null)
                                {
                                    objCyberware.Bonus = objNode["bonus"];
                                    objCyberware.WirelessBonus = objNode["wirelessbonus"];
                                    objCyberware.PairBonus = objNode["pairbonus"];
                                    if (!string.IsNullOrEmpty(objCyberware.Forced) && objCyberware.Forced != "Right"
                                        && objCyberware.Forced != "Left")
                                        ImprovementManager.ForcedValue = objCyberware.Forced;
                                    if (objCyberware.Bonus != null)
                                    {
                                        await ImprovementManager.CreateImprovementsAsync(
                                                                    CharacterObject, objCyberware.SourceType,
                                                                    objCyberware.InternalId,
                                                                    objCyberware.Bonus,
                                                                    await objCyberware.GetRatingAsync(token)
                                                                        .ConfigureAwait(false),
                                                                    await objCyberware
                                                                          .GetCurrentDisplayNameShortAsync(token)
                                                                          .ConfigureAwait(false), token: token)
                                                                .ConfigureAwait(false);
                                        if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue))
                                            objCyberware.Extra = ImprovementManager.SelectedValue;
                                    }

                                    if (!objCyberware.IsModularCurrentlyEquipped)
                                        await objCyberware.ChangeModularEquipAsync(false, token: token)
                                                          .ConfigureAwait(false);
                                    else
                                    {
                                        await objCyberware.RefreshWirelessBonusesAsync(token).ConfigureAwait(false);
                                        if (objCyberware.PairBonus != null)
                                        {
                                            Cyberware objMatchingCyberware
                                                = dicPairableCyberwares.Keys.FirstOrDefault(
                                                    x => objCyberware.IncludePair.Contains(x.Name)
                                                         && x.Extra == objCyberware.Extra);
                                            if (objMatchingCyberware != null)
                                                ++dicPairableCyberwares[objMatchingCyberware];
                                            else
                                                dicPairableCyberwares.Add(objCyberware, 1);
                                        }
                                    }

                                    TreeNode objWareNode = objCyberware.SourceID == Cyberware.EssenceHoleGUID
                                                           || objCyberware.SourceID == Cyberware.EssenceAntiHoleGUID
                                        ? await treCyberware.DoThreadSafeFuncAsync(
                                            x => x.FindNode(objCyberware.SourceIDString), token).ConfigureAwait(false)
                                        : await treCyberware.DoThreadSafeFuncAsync(
                                            x => x.FindNode(objCyberware.InternalId), token).ConfigureAwait(false);
                                    if (objWareNode != null)
                                    {
                                        string strText = await objCyberware.GetCurrentDisplayNameAsync(token).ConfigureAwait(false);
                                        await treCyberware.DoThreadSafeAsync(
                                                              () => objWareNode.Text = strText,
                                                              token)
                                                          .ConfigureAwait(false);
                                    }
                                }
                                else
                                {
                                    sbdOutdatedItems.AppendLine(await objCyberware.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                                }
                            }

                            foreach (Gear objGear in objCyberware.GearChildren)
                            {
                                await objGear.ReaddImprovements(treCyberware, sbdOutdatedItems, lstInternalIdFilter, token: token)
                                             .ConfigureAwait(false);
                            }
                        }

                        // Separate Pass for PairBonuses
                        foreach (KeyValuePair<Cyberware, int> objItem in dicPairableCyberwares)
                        {
                            Cyberware objCyberware = objItem.Key;
                            int intCyberwaresCount = objItem.Value;
                            List<Cyberware> lstPairableCyberwares = CharacterObject.Cyberware
                                .DeepWhere(x => x.Children,
                                           x => objCyberware.IncludePair.Contains(x.Name)
                                                && x.Extra == objCyberware.Extra && x.IsModularCurrentlyEquipped)
                                .ToList();
                            // Need to use slightly different logic if this cyberware has a location (Left or Right) and only pairs with itself because Lefts can only be paired with Rights and Rights only with Lefts
                            if (!string.IsNullOrEmpty(objCyberware.Location)
                                && objCyberware.IncludePair.All(x => x == objCyberware.Name))
                            {
                                int intMatchLocationCount = 0;
                                int intNotMatchLocationCount = 0;
                                foreach (Cyberware objPairableCyberware in lstPairableCyberwares)
                                {
                                    if (objPairableCyberware.Location != objCyberware.Location)
                                        ++intNotMatchLocationCount;
                                    else
                                        ++intMatchLocationCount;
                                }

                                // Set the count to the total number of cyberwares in matching pairs, which would mean 2x the number of whichever location contains the fewest members (since every single one of theirs would have a pair)
                                intCyberwaresCount = Math.Min(intNotMatchLocationCount, intMatchLocationCount) * 2;
                            }

                            if (intCyberwaresCount > 0)
                            {
                                foreach (Cyberware objLoopCyberware in lstPairableCyberwares)
                                {
                                    if ((intCyberwaresCount & 1) == 0)
                                    {
                                        if (!string.IsNullOrEmpty(objCyberware.Forced) && objCyberware.Forced != "Right"
                                            && objCyberware.Forced != "Left")
                                            ImprovementManager.ForcedValue = objCyberware.Forced;
                                        await ImprovementManager.CreateImprovementsAsync(
                                                                    CharacterObject, objLoopCyberware.SourceType,
                                                                    objLoopCyberware.InternalId + "Pair",
                                                                    objLoopCyberware.PairBonus,
                                                                    await objLoopCyberware.GetRatingAsync(token)
                                                                        .ConfigureAwait(false),
                                                                    await objLoopCyberware
                                                                          .GetCurrentDisplayNameShortAsync(token)
                                                                          .ConfigureAwait(false), token: token)
                                                                .ConfigureAwait(false);
                                        if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue)
                                            && string.IsNullOrEmpty(objCyberware.Extra))
                                            objCyberware.Extra = ImprovementManager.SelectedValue;
                                        TreeNode objNode = objLoopCyberware.SourceID == Cyberware.EssenceHoleGUID
                                                           || objCyberware.SourceID == Cyberware.EssenceAntiHoleGUID
                                            ? await treCyberware.DoThreadSafeFuncAsync(
                                                                    x => x.FindNode(objCyberware.SourceIDString), token)
                                                                .ConfigureAwait(false)
                                            : await treCyberware.DoThreadSafeFuncAsync(
                                                                    x => x.FindNode(objLoopCyberware.InternalId), token)
                                                                .ConfigureAwait(false);
                                        if (objNode != null)
                                        {
                                            string strName = await objLoopCyberware.GetCurrentDisplayNameAsync(token)
                                                .ConfigureAwait(false);
                                            await treCyberware.DoThreadSafeAsync(() => objNode.Text = strName, token)
                                                              .ConfigureAwait(false);
                                        }
                                    }

                                    --intCyberwaresCount;
                                    if (intCyberwaresCount <= 0)
                                        break;
                                }
                            }
                        }

                        // Refresh Armors.
                        foreach (Armor objArmor in CharacterObject.Armor)
                        {
                            // We're only re-apply improvements a list of items, not all of them
                            if (lstInternalIdFilter?.Contains(objArmor.InternalId) != false)
                            {
                                XmlNode objNode = await objArmor.GetNodeAsync(token).ConfigureAwait(false);
                                if (objNode != null)
                                {
                                    objArmor.Bonus = objNode["bonus"];
                                    if (objArmor.Bonus != null && objArmor.Equipped)
                                    {
                                        ImprovementManager.ForcedValue = objArmor.Extra;
                                        await ImprovementManager.CreateImprovementsAsync(
                                            CharacterObject, Improvement.ImprovementSource.Armor, objArmor.InternalId,
                                            objArmor.Bonus, objArmor.Rating,
                                            await objArmor.GetCurrentDisplayNameShortAsync(token).ConfigureAwait(false),
                                            token: token).ConfigureAwait(false);
                                        if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue))
                                        {
                                            objArmor.Extra = ImprovementManager.SelectedValue;
                                            string strName = await objArmor.GetCurrentDisplayNameAsync(token)
                                                                           .ConfigureAwait(false);
                                            await treArmor.DoThreadSafeAsync(x =>
                                            {
                                                TreeNode objArmorNode = x.FindNode(objArmor.InternalId);
                                                if (objArmorNode != null)
                                                    objArmorNode.Text = strName;
                                            }, token).ConfigureAwait(false);
                                        }
                                    }
                                }
                                else
                                {
                                    sbdOutdatedItems.AppendLine(
                                        await objArmor.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                                }
                            }

                            foreach (ArmorMod objMod in objArmor.ArmorMods)
                            {
                                // We're only re-apply improvements a list of items, not all of them
                                if (lstInternalIdFilter?.Contains(objMod.InternalId) != false)
                                {
                                    XmlNode objChild = await objMod.GetNodeAsync(token).ConfigureAwait(false);

                                    if (objChild != null)
                                    {
                                        objMod.Bonus = objChild["bonus"];
                                        if (objMod.Bonus != null && objMod.Equipped)
                                        {
                                            ImprovementManager.ForcedValue = objMod.Extra;
                                            await ImprovementManager.CreateImprovementsAsync(
                                                CharacterObject, Improvement.ImprovementSource.ArmorMod,
                                                objMod.InternalId, objMod.Bonus, objMod.Rating,
                                                await objMod.GetCurrentDisplayNameShortAsync(token)
                                                            .ConfigureAwait(false), token: token).ConfigureAwait(false);
                                            if (!string.IsNullOrEmpty(ImprovementManager.SelectedValue))
                                            {
                                                objMod.Extra = ImprovementManager.SelectedValue;
                                                string strName = await objMod.GetCurrentDisplayNameAsync(token)
                                                                             .ConfigureAwait(false);
                                                await treArmor.DoThreadSafeAsync(x =>
                                                {
                                                    TreeNode objPluginNode = x.FindNode(objMod.InternalId);
                                                    if (objPluginNode != null)
                                                        objPluginNode.Text = strName;
                                                }, token).ConfigureAwait(false);
                                            }
                                        }
                                    }
                                    else
                                    {
                                        sbdOutdatedItems.AppendLine(
                                            await objMod.GetCurrentDisplayNameAsync(token).ConfigureAwait(false));
                                    }
                                }

                                foreach (Gear objGear in objMod.GearChildren)
                                {
                                    await objGear.ReaddImprovements(treArmor, sbdOutdatedItems, lstInternalIdFilter, token: token)
                                                 .ConfigureAwait(false);
                                }
                            }

                            foreach (Gear objGear in objArmor.GearChildren)
                            {
                                await objGear.ReaddImprovements(treArmor, sbdOutdatedItems, lstInternalIdFilter, token: token)
                                             .ConfigureAwait(false);
                            }

                            await objArmor.RefreshWirelessBonusesAsync(token).ConfigureAwait(false);
                        }

                        // Refresh Gear.
                        foreach (Gear objGear in CharacterObject.Gear)
                        {
                            await objGear.ReaddImprovements(treGear, sbdOutdatedItems, lstInternalIdFilter, token: token)
                                         .ConfigureAwait(false);
                            await objGear.RefreshWirelessBonusesAsync(token).ConfigureAwait(false);
                        }

                        // Refresh Weapons Gear
                        foreach (Weapon objWeapon in CharacterObject.Weapons)
                        {
                            foreach (WeaponAccessory objAccessory in objWeapon.WeaponAccessories)
                            {
                                foreach (Gear objGear in objAccessory.GearChildren)
                                {
                                    await objGear.ReaddImprovements(treWeapons, sbdOutdatedItems, lstInternalIdFilter, token: token)
                                                 .ConfigureAwait(false);
                                }
                            }

                            await objWeapon.RefreshWirelessBonusesAsync(token).ConfigureAwait(false);
                        }

                        _blnReapplyImprovements = false;

                        // If the status of any Character Event flags has changed, manually trigger those events.
                        if (blnMAGEnabled != CharacterObject.MAGEnabled)
                        {
                            CharacterObject.EssenceAtSpecialStart = decEssenceAtSpecialStart;
                            await DoOnCharacterPropertyChanged(
                                new PropertyChangedEventArgs(nameof(Character.MAGEnabled))).ConfigureAwait(false);
                        }

                        if (blnRESEnabled != CharacterObject.RESEnabled)
                        {
                            CharacterObject.EssenceAtSpecialStart = decEssenceAtSpecialStart;
                            await DoOnCharacterPropertyChanged(
                                new PropertyChangedEventArgs(nameof(Character.RESEnabled))).ConfigureAwait(false);
                        }

                        if (blnDEPEnabled != CharacterObject.DEPEnabled)
                        {
                            CharacterObject.EssenceAtSpecialStart = decEssenceAtSpecialStart;
                            await DoOnCharacterPropertyChanged(
                                new PropertyChangedEventArgs(nameof(Character.DEPEnabled))).ConfigureAwait(false);
                        }

                        await RequestCharacterUpdate(token).ConfigureAwait(false);
                        // Immediately await character update because it re-applies essence loss improvements
                        try
                        {
                            await UpdateCharacterInfoTask.ConfigureAwait(false);
                        }
                        catch (OperationCanceledException)
                        {
                            return;
                        }

                        if (sbdOutdatedItems.Length > 0 && !Utils.IsUnitTest)
                        {
                            Program.ShowMessageBox(
                                this, await LanguageManager.GetStringAsync(
                                                               "Message_ReapplyImprovementsFoundOutdatedItems_Top",
                                                               token: token)
                                                           .ConfigureAwait(false) +
                                      sbdOutdatedItems +
                                      await LanguageManager.GetStringAsync(
                                                               "Message_ReapplyImprovementsFoundOutdatedItems_Bottom",
                                                               token: token)
                                                           .ConfigureAwait(false),
                                await LanguageManager
                                      .GetStringAsync("MessageTitle_ConfirmReapplyImprovements", token: token)
                                      .ConfigureAwait(false),
                                MessageBoxButtons.OK,
                                MessageBoxIcon.Error);
                        }
                    }
                }
                finally
                {
                    await objLocker.DisposeAsync().ConfigureAwait(false);
                }
            }
            finally
            {
                await objCursorWait.DisposeAsync().ConfigureAwait(false);
            }

            await SetDirty(true, token).ConfigureAwait(false);
        }

        private async void mnuSpecialPossess_Click(object sender, EventArgs e)
        {
            // Make sure the Spirit has been saved first.
            if (IsDirty && Program.ShowMessageBox(this, await LanguageManager.GetStringAsync("Message_PossessionSave").ConfigureAwait(false), await LanguageManager.GetStringAsync("MessageTitle_Possession").ConfigureAwait(false), MessageBoxButtons.YesNo, MessageBoxIcon.Question) == DialogResult.No)
                return;

            if (Utils.IsUnitTest)
            {
                Utils.BreakIfDebug();
                return;
            }

            try
            {
                string strFileName = string.Empty;
                string strFilter = await LanguageManager.GetStringAsync("DialogFilter_Chummer").ConfigureAwait(false)
                                   + '|' +
                                   await LanguageManager.GetStringAsync("DialogFilter_Chum5").ConfigureAwait(false)
                                   + '|' +
                                   await LanguageManager.GetStringAsync("DialogFilter_Chum5lz").ConfigureAwait(false)
                                   + '|' +
                                   await LanguageManager.GetStringAsync("DialogFilter_All").ConfigureAwait(false);
                // Prompt the user to select a save file to possess.
                DialogResult eResult = await this.DoThreadSafeFuncAsync(x =>
                {
                    using (OpenFileDialog dlgOpenFile = new OpenFileDialog())
                    {
                        dlgOpenFile.Filter = strFilter;
                        DialogResult eReturn = dlgOpenFile.ShowDialog(x);
                        strFileName = dlgOpenFile.FileName;
                        return eReturn;
                    }
                }, GenericToken).ConfigureAwait(false);
                if (eResult != DialogResult.OK)
                    return;

                string strOpenFile = string.Empty;
                CursorWait objCursorWait = await CursorWait.NewAsync(this, token: GenericToken).ConfigureAwait(false);
                try
                {
                    Character objMerge = new Character {FileName = CharacterObject.FileName};
                    try
                    {
                        Character objVessel = new Character {FileName = strFileName};
                        try
                        {
                            using (ThreadSafeForm<LoadingBar> frmLoadingBar
                                   = await Program.CreateAndShowProgressBarAsync(
                                       Path.GetFileName(objVessel.FileName), Character.NumLoadingSections * 2 + 7, GenericToken).ConfigureAwait(false))
                            {
                                bool blnSuccess = await objVessel.LoadAsync(frmLoadingForm: frmLoadingBar.MyForm, token: GenericToken).ConfigureAwait(false);
                                if (!blnSuccess)
                                {
                                    Program.ShowMessageBox(this,
                                                           await LanguageManager.GetStringAsync(
                                                               "Message_Load_Error_Warning").ConfigureAwait(false),
                                                           await LanguageManager.GetStringAsync("String_Error").ConfigureAwait(false),
                                                           MessageBoxButtons.OK,
                                                           MessageBoxIcon.Error);
                                    return;
                                }

                                // Make sure the Vessel is in Career Mode.
                                if (!objVessel.Created)
                                {
                                    Program.ShowMessageBox(this,
                                                           await LanguageManager.GetStringAsync(
                                                               "Message_VesselInCareerMode").ConfigureAwait(false),
                                                           await LanguageManager.GetStringAsync(
                                                               "MessageTitle_Possession").ConfigureAwait(false),
                                                           MessageBoxButtons.OK,
                                                           MessageBoxIcon.Error);
                                    return;
                                }

                                // Load the Spirit's save file into a new Merge character.
                                frmLoadingBar.MyForm.CharacterFile = objMerge.FileName;
                                blnSuccess = await objMerge.LoadAsync(frmLoadingForm: frmLoadingBar.MyForm, token: GenericToken).ConfigureAwait(false);
                                if (!blnSuccess)
                                {
                                    Program.ShowMessageBox(this,
                                                           await LanguageManager.GetStringAsync(
                                                               "Message_Load_Error_Warning").ConfigureAwait(false),
                                                           await LanguageManager.GetStringAsync("String_Error").ConfigureAwait(false),
                                                           MessageBoxButtons.OK,
                                                           MessageBoxIcon.Error);
                                    return;
                                }

                                objMerge.Possessed = true;
                                objMerge.Alias = objVessel.CharacterName
                                                 + await LanguageManager.GetStringAsync("String_Space").ConfigureAwait(false) + '('
                                                 + await LanguageManager.GetStringAsync("String_Possessed").ConfigureAwait(false) + ')';

                                // Give the Critter the Immunity to Normal Weapons Power if they don't already have it.
                                bool blnHasImmunity = false;
                                foreach (CritterPower objCritterPower in objMerge.CritterPowers)
                                {
                                    if (objCritterPower.Name == "Immunity" && objCritterPower.Extra == "Normal Weapons")
                                    {
                                        blnHasImmunity = true;
                                        break;
                                    }
                                }

                                if (!blnHasImmunity)
                                {
                                    XmlDocument objPowerDoc
                                        = await CharacterObject.LoadDataAsync("critterpowers.xml", token: GenericToken).ConfigureAwait(false);
                                    XmlNode objPower
                                        = objPowerDoc.SelectSingleNode("/chummer/powers/power[name = \"Immunity\"]");

                                    CritterPower objCritterPower = new CritterPower(objMerge);
                                    objCritterPower.Create(objPower, 0, "Normal Weapons");
                                    await objMerge.CritterPowers.AddAsync(objCritterPower).ConfigureAwait(false);
                                }

                                //TOD: Implement Possession attribute bonuses.
                                /*
                            // Add the Vessel's Physical Attributes to the Spirit's Force.
                            objMerge.BOD.MetatypeMaximum = objVessel.BOD.Value + objMerge.MAG.TotalValue;
                            objMerge.BOD.Value = objVessel.BOD.Value + objMerge.MAG.TotalValue;
                            objMerge.AGI.MetatypeMaximum = objVessel.AGI.Value + objMerge.MAG.TotalValue;
                            objMerge.AGI.Value = objVessel.AGI.Value + objMerge.MAG.TotalValue;
                            objMerge.REA.MetatypeMaximum = objVessel.REA.Value + objMerge.MAG.TotalValue;
                            objMerge.REA.Value = objVessel.REA.Value + objMerge.MAG.TotalValue;
                            objMerge.STR.MetatypeMaximum = objVessel.STR.Value + objMerge.MAG.TotalValue;
                            objMerge.STR.Value = objVessel.STR.Value + objMerge.MAG.TotalValue;
                            */

                                await frmLoadingBar.MyForm.PerformStepAsync(
                                    await LanguageManager.GetStringAsync("String_SelectPACKSKit_Lifestyles").ConfigureAwait(false),
                                    token: GenericToken).ConfigureAwait(false);
                                // Copy any Lifestyles the Vessel has.
                                foreach (Lifestyle objLifestyle in objVessel.Lifestyles)
                                    await objMerge.Lifestyles.AddAsync(objLifestyle).ConfigureAwait(false);

                                await frmLoadingBar.MyForm.PerformStepAsync(
                                    await LanguageManager.GetStringAsync("Tab_Armor").ConfigureAwait(false), token: GenericToken).ConfigureAwait(false);
                                // Copy any Armor the Vessel has.
                                foreach (Armor objArmor in objVessel.Armor)
                                {
                                    await objMerge.Armor.AddAsync(objArmor).ConfigureAwait(false);
                                    CopyArmorImprovements(objVessel, objMerge, objArmor);
                                }

                                await frmLoadingBar.MyForm.PerformStepAsync(
                                    await LanguageManager.GetStringAsync("Tab_Gear").ConfigureAwait(false), token: GenericToken).ConfigureAwait(false);
                                // Copy any Gear the Vessel has.
                                foreach (Gear objGear in objVessel.Gear)
                                {
                                    await objMerge.Gear.AddAsync(objGear).ConfigureAwait(false);
                                    CopyGearImprovements(objVessel, objMerge, objGear);
                                }

                                await frmLoadingBar.MyForm.PerformStepAsync(
                                    await LanguageManager.GetStringAsync("Tab_Cyberware").ConfigureAwait(false), token: GenericToken).ConfigureAwait(false);
                                // Copy any Cyberware/Bioware the Vessel has.
                                foreach (Cyberware objCyberware in objVessel.Cyberware)
                                {
                                    await objMerge.Cyberware.AddAsync(objCyberware).ConfigureAwait(false);
                                    CopyCyberwareImprovements(objVessel, objMerge, objCyberware);
                                }

                                await frmLoadingBar.MyForm.PerformStepAsync(
                                    await LanguageManager.GetStringAsync("Tab_Weapons").ConfigureAwait(false), token: GenericToken).ConfigureAwait(false);
                                // Copy any Weapons the Vessel has.
                                foreach (Weapon objWeapon in objVessel.Weapons)
                                    await objMerge.Weapons.AddAsync(objWeapon).ConfigureAwait(false);

                                await frmLoadingBar.MyForm.PerformStepAsync(
                                    await LanguageManager.GetStringAsync("Tab_Vehicles").ConfigureAwait(false), token: GenericToken).ConfigureAwait(false);
                                // Copy and Vehicles the Vessel has.
                                foreach (Vehicle objVehicle in objVessel.Vehicles)
                                    await objMerge.Vehicles.AddAsync(objVehicle).ConfigureAwait(false);

                                await frmLoadingBar.MyForm.PerformStepAsync(
                                    await LanguageManager.GetStringAsync("String_Settings").ConfigureAwait(false), token: GenericToken).ConfigureAwait(false);
                                // Copy the character info.
                                objMerge.Gender = objVessel.Gender;
                                objMerge.Age = objVessel.Age;
                                objMerge.Eyes = objVessel.Eyes;
                                objMerge.Hair = objVessel.Hair;
                                objMerge.Height = objVessel.Height;
                                objMerge.Weight = objVessel.Weight;
                                objMerge.Skin = objVessel.Skin;
                                objMerge.Name = objVessel.Name;
                                objMerge.StreetCred = objVessel.StreetCred;
                                objMerge.BurntStreetCred = objVessel.BurntStreetCred;
                                objMerge.Notoriety = objVessel.Notoriety;
                                objMerge.PublicAwareness = objVessel.PublicAwareness;
                                foreach (Image objMugshot in objVessel.Mugshots)
                                    await objMerge.Mugshots.AddAsync(objMugshot).ConfigureAwait(false);
                            }
                        }
                        finally
                        {
                            await objVessel.DisposeAsync().ConfigureAwait(false);
                        }

                        string strShowFileName = Path.GetFileName(objMerge.FileName);
                        if (string.IsNullOrEmpty(strShowFileName))
                            strShowFileName = objMerge.CharacterName;
                        strShowFileName = strShowFileName.TrimEndOnce(".chum5").TrimEndOnce(".chum5lz")
                                          + await LanguageManager.GetStringAsync("String_Space").ConfigureAwait(false) + '('
                                          + await LanguageManager.GetStringAsync("String_Possessed").ConfigureAwait(false) + ')';

                        // Now that everything is done, save the merged character and open them.
                        dlgSaveFile.FileName = strShowFileName;
                        if (await this.DoThreadSafeFuncAsync(x => dlgSaveFile.ShowDialog(x), GenericToken).ConfigureAwait(false)
                            != DialogResult.OK)
                            return;
                        using (ThreadSafeForm<LoadingBar> frmLoadingBar = await Program.CreateAndShowProgressBarAsync(token: GenericToken).ConfigureAwait(false))
                        {
                            await frmLoadingBar.MyForm.PerformStepAsync(objMerge.CharacterName,
                                                                        LoadingBar.ProgressBarTextPatterns.Saving,
                                                                        token: GenericToken).ConfigureAwait(false);
                            objMerge.FileName = dlgSaveFile.FileName;
                            if (await objMerge.SaveAsync(token: GenericToken).ConfigureAwait(false))
                            {
                                // Get the name of the file and destroy the references to the Vessel and the merged character.
                                strOpenFile = objMerge.FileName;
                            }
                        }
                    }
                    finally
                    {
                        await objMerge
                              .DisposeAsync().ConfigureAwait(false); // Fine here because Dispose()/DisposeAsync() code is skipped if the character is open in a form
                    }
                }
                finally
                {
                    await objCursorWait.DisposeAsync().ConfigureAwait(false);
                }

                if (!string.IsNullOrEmpty(strOpenFile))
                {
                    objCursorWait = await CursorWait.NewAsync(this, token: GenericToken).ConfigureAwait(false);
                    try
                    {
                        Character objOpenCharacter;
                        using (ThreadSafeForm<LoadingBar> frmLoadingBar
                               = await Program.CreateAndShowProgressBarAsync(strOpenFile, Character.NumLoadingSections, GenericToken).ConfigureAwait(false))
                            objOpenCharacter
                                = await Program.LoadCharacterAsync(strOpenFile, frmLoadingBar: frmLoadingBar.MyForm,
                                                                   token: GenericToken).ConfigureAwait(false);
                        await Program.OpenCharacter(objOpenCharacter, token: GenericToken).ConfigureAwait(false);
                    }
                    finally
                    {
                        await objCursorWait.DisposeAsync().ConfigureAwait(false);
                    }
                }
            }
            catch (OperationCanceledException)
            {
                //swallow this
            }
        }

        private async void mnuSpecialPossessInanimate_Click(object sender, EventArgs e)
        {
            // Make sure the Spirit has been saved first.
            if (IsDirty && Program.ShowMessageBox(this, await LanguageManager.GetStringAsync("Message_PossessionSave").ConfigureAwait(false),
                                                  await LanguageManager.GetStringAsync("MessageTitle_Possession").ConfigureAwait(false),
                                                  MessageBoxButtons.YesNo, MessageBoxIcon.Question) == DialogResult.No)
                return;

            if (Utils.IsUnitTest)
            {
                Utils.BreakIfDebug();
                return;
            }

            try
            {
                string strSelectedVessel;
                // Prompt the user to select an inanimate Vessel.
                XPathNavigator xmlVesselsNavigator = await CharacterObject.LoadDataXPathAsync("vessels.xml", token: GenericToken).ConfigureAwait(false);
                using (new FetchSafelyFromPool<List<ListItem>>(Utils.ListItemListPool, out List<ListItem> lstMetatype))
                {
                    foreach (XPathNavigator xmlMetatype in await xmlVesselsNavigator.SelectAndCacheExpressionAsync(
                                 "/chummer/metatypes/metatype").ConfigureAwait(false))
                    {
                        string strName = (await xmlMetatype.SelectSingleNodeAndCacheExpressionAsync("name").ConfigureAwait(false))?.Value;
                        if (!string.IsNullOrEmpty(strName))
                        {
                            ListItem objItem
                                = new ListItem(
                                    strName, (await xmlMetatype.SelectSingleNodeAndCacheExpressionAsync("translate").ConfigureAwait(false))?.Value ?? strName);
                            lstMetatype.Add(objItem);
                        }
                    }

                    using (ThreadSafeForm<SelectItem> frmSelectVessel = await ThreadSafeForm<SelectItem>.GetAsync(() => new SelectItem(), GenericToken).ConfigureAwait(false))
                    {
                        frmSelectVessel.MyForm.SetGeneralItemsMode(lstMetatype);

                        if (await frmSelectVessel.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false) == DialogResult.Cancel)
                            return;

                        strSelectedVessel = frmSelectVessel.MyForm.SelectedItem;
                    }
                }

                // Get the Node for the selected Vessel.
                XmlDocument xmlVessels = await CharacterObject.LoadDataAsync("vessels.xml", token: GenericToken).ConfigureAwait(false);
                XmlNode objSelected = xmlVessels.SelectSingleNode("/chummer/metatypes/metatype[name = " + strSelectedVessel.CleanXPath() + ']');
                if (objSelected == null)
                    return;

                string strOpenFile = string.Empty;
                CursorWait objCursorWait = await CursorWait.NewAsync(this, token: GenericToken).ConfigureAwait(false);
                try
                {
                    // Load the Spirit's save file into a new Merge character.
                    Character objMerge = new Character {FileName = CharacterObject.FileName};
                    try
                    {
                        using (ThreadSafeForm<LoadingBar> frmLoadingBar
                               = await Program.CreateAndShowProgressBarAsync(
                                   objMerge.FileName, Character.NumLoadingSections + 1, GenericToken).ConfigureAwait(false))
                        {
                            await objMerge.LoadAsync(frmLoadingForm: frmLoadingBar.MyForm, token: GenericToken).ConfigureAwait(false);
                            await frmLoadingBar.MyForm.PerformStepAsync(
                                await LanguageManager.GetStringAsync("String_UI").ConfigureAwait(false), token: GenericToken).ConfigureAwait(false);
                            objMerge.Possessed = true;
                            objMerge.Alias = strSelectedVessel + await LanguageManager.GetStringAsync("String_Space").ConfigureAwait(false)
                                                               + '('
                                                               + await LanguageManager.GetStringAsync(
                                                                   "String_Possessed").ConfigureAwait(false) + ')';

                            int intHalfMAGRoundedUp
                                = (await (await CharacterObject.GetAttributeAsync("MAG", token: GenericToken).ConfigureAwait(false))
                                         .GetTotalValueAsync(GenericToken).ConfigureAwait(false)).DivAwayFromZero(2);
                            await ImprovementManager.CreateImprovementAsync(
                                objMerge, "BOD", Improvement.ImprovementSource.Metatype, "Possession",
                                Improvement.ImprovementType.Attribute, string.Empty, intHalfMAGRoundedUp, 1, 0, 0,
                                intHalfMAGRoundedUp, intHalfMAGRoundedUp).ConfigureAwait(false);
                            await ImprovementManager.CreateImprovementAsync(
                                objMerge, "AGI", Improvement.ImprovementSource.Metatype, "Possession",
                                Improvement.ImprovementType.Attribute, string.Empty, intHalfMAGRoundedUp, 1, 0, 0,
                                intHalfMAGRoundedUp, intHalfMAGRoundedUp).ConfigureAwait(false);
                            await ImprovementManager.CreateImprovementAsync(
                                objMerge, "STR", Improvement.ImprovementSource.Metatype, "Possession",
                                Improvement.ImprovementType.Attribute, string.Empty, intHalfMAGRoundedUp, 1, 0, 0,
                                intHalfMAGRoundedUp, intHalfMAGRoundedUp).ConfigureAwait(false);
                            await ImprovementManager.CreateImprovementAsync(
                                objMerge, "REA", Improvement.ImprovementSource.Metatype, "Possession",
                                Improvement.ImprovementType.Attribute, string.Empty, intHalfMAGRoundedUp, 1, 0, 0,
                                intHalfMAGRoundedUp, intHalfMAGRoundedUp).ConfigureAwait(false);
                            await ImprovementManager.CreateImprovementAsync(
                                objMerge, "INT", Improvement.ImprovementSource.Metatype, "Possession",
                                Improvement.ImprovementType.ReplaceAttribute, string.Empty, 0, 1,
                                CharacterObject.INT.MetatypeMinimum,
                                CharacterObject.INT.MetatypeMaximum, 0, CharacterObject.INT.MetatypeAugmentedMaximum).ConfigureAwait(false);
                            await ImprovementManager.CreateImprovementAsync(
                                objMerge, "WIL", Improvement.ImprovementSource.Metatype, "Possession",
                                Improvement.ImprovementType.ReplaceAttribute, string.Empty, 0, 1,
                                CharacterObject.WIL.MetatypeMinimum,
                                CharacterObject.WIL.MetatypeMaximum, 0, CharacterObject.WIL.MetatypeAugmentedMaximum).ConfigureAwait(false);
                            await ImprovementManager.CreateImprovementAsync(
                                objMerge, "LOG", Improvement.ImprovementSource.Metatype, "Possession",
                                Improvement.ImprovementType.ReplaceAttribute, string.Empty, 0, 1,
                                CharacterObject.LOG.MetatypeMinimum,
                                CharacterObject.LOG.MetatypeMaximum, 0, CharacterObject.LOG.MetatypeAugmentedMaximum).ConfigureAwait(false);
                            await ImprovementManager.CreateImprovementAsync(
                                objMerge, "CHA", Improvement.ImprovementSource.Metatype, "Possession",
                                Improvement.ImprovementType.ReplaceAttribute, string.Empty, 0, 1,
                                CharacterObject.CHA.MetatypeMinimum,
                                CharacterObject.CHA.MetatypeMaximum, 0, CharacterObject.CHA.MetatypeAugmentedMaximum).ConfigureAwait(false);
                            await ImprovementManager.CommitAsync(objMerge).ConfigureAwait(false);
                            XmlDocument xmlPowerDoc
                                = await CharacterObject.LoadDataAsync("critterpowers.xml", token: GenericToken).ConfigureAwait(false);

                            // Update the Movement if the Vessel has one.
                            string strMovement = objSelected["movement"]?.InnerText;
                            if (!string.IsNullOrEmpty(strMovement))
                                objMerge.Movement = strMovement;

                            // Add any additional Critter Powers the Vessel grants.
                            XmlNode xmlPowersNode = objSelected["powers"];
                            if (xmlPowersNode != null)
                            {
                                using (XmlNodeList xmlPowerList = xmlPowersNode.SelectNodes("power"))
                                {
                                    if (xmlPowerList?.Count > 0)
                                    {
                                        foreach (XmlNode objXmlPower in xmlPowerList)
                                        {
                                            XmlNode objXmlCritterPower
                                                = xmlPowerDoc.SelectSingleNode(
                                                    "/chummer/powers/power[name = " + objXmlPower.InnerText.CleanXPath()
                                                    + ']');
                                            CritterPower objPower = new CritterPower(objMerge);
                                            string strSelect = objXmlPower.Attributes?["select"]?.InnerText
                                                               ?? string.Empty;
                                            int intRating = Convert.ToInt32(
                                                objXmlPower.Attributes?["rating"]?.InnerText,
                                                GlobalSettings.InvariantCultureInfo);

                                            objPower.Create(objXmlCritterPower, intRating, strSelect);

                                            await objMerge.CritterPowers.AddAsync(objPower, GenericToken).ConfigureAwait(false);
                                        }
                                    }
                                }
                            }

                            // Give the Critter the Immunity to Normal Weapons Power if they don't already have it.
                            if (!await objMerge.CritterPowers.AnyAsync(objCritterPower =>
                                                                           objCritterPower.Name == "Immunity"
                                                                           && objCritterPower.Extra == "Normal Weapons", GenericToken).ConfigureAwait(false))
                            {
                                XmlNode objPower
                                    = xmlPowerDoc.SelectSingleNode("/chummer/powers/power[name = \"Immunity\"]");

                                CritterPower objCritterPower = new CritterPower(objMerge);
                                objCritterPower.Create(objPower, 0, "Normal Weapons");
                                await objMerge.CritterPowers.AddAsync(objCritterPower, GenericToken).ConfigureAwait(false);
                            }

                            // Add any Improvements the Vessel grants.
                            if (objSelected["bonus"] != null)
                            {
                                await ImprovementManager.CreateImprovementsAsync(
                                    objMerge, Improvement.ImprovementSource.Metatype, strSelectedVessel,
                                    objSelected["bonus"], 1, strSelectedVessel).ConfigureAwait(false);
                            }
                        }

                        // Now that everything is done, save the merged character and open them.
                        string strShowFileName = Path.GetFileName(objMerge.FileName);

                        if (string.IsNullOrEmpty(strShowFileName))
                        {
                            strShowFileName = objMerge.CharacterName.CleanForFileName();
                        }

                        strShowFileName = strShowFileName.TrimEndOnce(".chum5").TrimEndOnce(".chum5lz");

                        strShowFileName += await LanguageManager.GetStringAsync("String_Space").ConfigureAwait(false) + '('
                            + await LanguageManager.GetStringAsync("String_Possessed").ConfigureAwait(false) + ')';
                        dlgSaveFile.FileName = strShowFileName;
                        if (await this.DoThreadSafeFuncAsync(x => dlgSaveFile.ShowDialog(x), GenericToken).ConfigureAwait(false)
                            != DialogResult.OK)
                            return;
                        using (ThreadSafeForm<LoadingBar> frmLoadingBar
                               = await Program.CreateAndShowProgressBarAsync(token: GenericToken).ConfigureAwait(false))
                        {
                            await frmLoadingBar.MyForm.PerformStepAsync(objMerge.CharacterName,
                                                                        LoadingBar.ProgressBarTextPatterns.Saving,
                                                                        token: GenericToken).ConfigureAwait(false);
                            objMerge.FileName = dlgSaveFile.FileName;
                            if (await objMerge.SaveAsync(token: GenericToken).ConfigureAwait(false))
                            {
                                // Get the name of the file and destroy the references to the Vessel and the merged character.
                                strOpenFile = objMerge.FileName;
                            }
                        }
                    }
                    finally
                    {
                        await objMerge
                              .DisposeAsync().ConfigureAwait(false); // Fine here because Dispose()/DisposeAsync() code is skipped if the character is open in a form
                    }
                }
                finally
                {
                    await objCursorWait.DisposeAsync().ConfigureAwait(false);
                }

                if (!string.IsNullOrEmpty(strOpenFile))
                {
                    objCursorWait = await CursorWait.NewAsync(this, token: GenericToken).ConfigureAwait(false);
                    try
                    {
                        Character objOpenCharacter;
                        using (ThreadSafeForm<LoadingBar> frmLoadingBar
                               = await Program.CreateAndShowProgressBarAsync(
                                   strOpenFile, Character.NumLoadingSections, GenericToken).ConfigureAwait(false))
                            objOpenCharacter
                                = await Program.LoadCharacterAsync(strOpenFile, frmLoadingBar: frmLoadingBar.MyForm,
                                                                   token: GenericToken).ConfigureAwait(false);
                        await Program.OpenCharacter(objOpenCharacter, token: GenericToken).ConfigureAwait(false);
                    }
                    finally
                    {
                        await objCursorWait.DisposeAsync().ConfigureAwait(false);
                    }
                }
            }
            catch (OperationCanceledException)
            {
                //swallow this
            }
        }

        private void mnuEditCopy_Click(object sender, EventArgs e)
        {
            object selectedObject = null;
            if (tabCharacterTabs.SelectedTab == tabStreetGear)
            {
                // Lifestyle Tab.
                if (tabStreetGearTabs.SelectedTab == tabLifestyle)
                {
                    selectedObject = treLifestyles.SelectedNode?.Tag;
                }
                // Armor Tab.
                else if (tabStreetGearTabs.SelectedTab == tabArmor)
                {
                    selectedObject = treArmor.SelectedNode?.Tag;
                }
                // Weapons Tab.
                else if (tabStreetGearTabs.SelectedTab == tabWeapons)
                {
                    selectedObject = treWeapons.SelectedNode?.Tag;
                }
                // Gear Tab.
                else if (tabStreetGearTabs.SelectedTab == tabGear)
                {
                    selectedObject = treGear.SelectedNode?.Tag;
                }
            }
            // Cyberware Tab.
            else if (tabCharacterTabs.SelectedTab == tabCyberware)
            {
                selectedObject = treCyberware.SelectedNode?.Tag;
            }
            // Vehicles Tab.
            else if (tabCharacterTabs.SelectedTab == tabVehicles)
            {
                selectedObject = treVehicles.SelectedNode?.Tag;
            }

            CopyObject(selectedObject);
        }

