        private async void cmdGearReduceQty_Click(object sender, EventArgs e)
        {
            try
            {
                TreeNode objSelectedNode = await treGear.DoThreadSafeFuncAsync(x => x.SelectedNode, GenericToken)
                                                        .ConfigureAwait(false);
                if (!(objSelectedNode?.Tag is Gear objGear))
                    return;

                int intDecimalPlaces = 0;
                if (objGear.Name.StartsWith("Nuyen", StringComparison.Ordinal))
                {
                    intDecimalPlaces = CharacterObjectSettings.MaxNuyenDecimals;
                }
                else if (objGear.Category == "Currency")
                {
                    intDecimalPlaces = 2;
                }

                decimal decSelectedValue;
                string strDescription = await LanguageManager.GetStringAsync("String_ReduceGear", token: GenericToken)
                                                             .ConfigureAwait(false);
                using (ThreadSafeForm<SelectNumber> frmPickNumber = await ThreadSafeForm<SelectNumber>.GetAsync(
                           () => new SelectNumber(intDecimalPlaces)
                           {
                               Minimum = 0,
                               Maximum = objGear.Quantity,
                               Description = strDescription
                           }, GenericToken).ConfigureAwait(false))
                {
                    if (await frmPickNumber.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false)
                        == DialogResult.Cancel)
                        return;

                    decSelectedValue = frmPickNumber.MyForm.SelectedValue;
                }

                if (!await CommonFunctions.ConfirmDeleteAsync(string.Format(GlobalSettings.CultureInfo,
                                                                            await LanguageManager.GetStringAsync(
                                                                                    "Message_ReduceQty",
                                                                                    token: GenericToken)
                                                                                .ConfigureAwait(false),
                                                                            decSelectedValue.ToString(
                                                                                GlobalSettings.CultureInfo)))
                                          .ConfigureAwait(false))
                    return;

                objGear.Quantity -= decSelectedValue;

                if (objGear.Quantity > 0)
                {
                    string strText = await objGear.GetCurrentDisplayNameAsync(GenericToken).ConfigureAwait(false);
                    await treGear.DoThreadSafeAsync(() => objSelectedNode.Text = strText, GenericToken)
                                 .ConfigureAwait(false);
                    await RequestCharacterUpdate().ConfigureAwait(false);
                    await SetDirty(true).ConfigureAwait(false);
                }
                else
                {
                    // Remove any Weapons that came with it.
                    await objGear.DeleteGearAsync(token: GenericToken).ConfigureAwait(false);
                }
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

        private async void cmdGearSplitQty_Click(object sender, EventArgs e)
        {
            try
            {
                TreeNode objSelectedNode = await treGear.DoThreadSafeFuncAsync(x => x.SelectedNode, GenericToken).ConfigureAwait(false);
                if (!(objSelectedNode?.Tag is Gear objSelectedGear))
                    return;

                decimal decMinimumAmount = 1.0m;
                int intDecimalPlaces = 0;
                if (objSelectedGear.Name.StartsWith("Nuyen", StringComparison.Ordinal))
                {
                    intDecimalPlaces = Math.Max(0, CharacterObjectSettings.MaxNuyenDecimals);
                    // Need a for loop instead of a power system to maintain exact precision
                    for (int i = 0; i < intDecimalPlaces; ++i)
                        decMinimumAmount /= 10.0m;
                }
                else if (objSelectedGear.Category == "Currency")
                {
                    intDecimalPlaces = 2;
                    decMinimumAmount = 0.01m;
                }
                // Cannot split a stack of 1 item.
                if (objSelectedGear.Quantity <= decMinimumAmount)
                {
                    Program.ShowMessageBox(
                        this, await LanguageManager.GetStringAsync("Message_CannotSplitGear", token: GenericToken).ConfigureAwait(false),
                        await LanguageManager.GetStringAsync("MessageTitle_CannotSplitGear", token: GenericToken).ConfigureAwait(false),
                        MessageBoxButtons.OK, MessageBoxIcon.Error);
                    return;
                }

                string strDescription = await LanguageManager.GetStringAsync("String_SplitGear", token: GenericToken).ConfigureAwait(false);
                using (ThreadSafeForm<SelectNumber> frmPickNumber = await ThreadSafeForm<SelectNumber>.GetAsync(
                           () => new SelectNumber(intDecimalPlaces)
                           {
                               Minimum = decMinimumAmount,
                               Maximum = objSelectedGear.Quantity - decMinimumAmount,
                               Description = strDescription
                           }, GenericToken).ConfigureAwait(false))
                {
                    if (await frmPickNumber.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false) == DialogResult.Cancel)
                        return;

                    // Create a new piece of Gear.
                    Gear objGear = new Gear(CharacterObject);

                    objGear.Copy(objSelectedGear);

                    objGear.Quantity = frmPickNumber.MyForm.SelectedValue;
                    objGear.Equipped = objSelectedGear.Equipped;
                    objGear.Location = objSelectedGear.Location;
                    objGear.Notes = objSelectedGear.Notes;

                    // Update the selected item.
                    objSelectedGear.Quantity -= objGear.Quantity;
                    string strText = await objGear.GetCurrentDisplayNameAsync(GenericToken).ConfigureAwait(false);
                    await treGear.DoThreadSafeAsync(() => objSelectedNode.Text = strText, GenericToken)
                                 .ConfigureAwait(false);

                    await CharacterObject.Gear.AddAsync(objGear, GenericToken).ConfigureAwait(false);
                }
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

        private async void cmdGearMergeQty_Click(object sender, EventArgs e)
        {
            try
            {
                TreeNode objSelectedNode = await treGear.DoThreadSafeFuncAsync(x => x.SelectedNode, GenericToken).ConfigureAwait(false);
                if (!(objSelectedNode?.Tag is Gear objGear))
                    return;
                List<Gear> lstGear = new List<Gear>(CharacterObject.Gear.Count);

                foreach (Gear objCharacterGear in CharacterObject.Gear)
                {
                    if (objCharacterGear.InternalId != objGear.InternalId
                        && objCharacterGear.IsIdenticalToOtherGear(objGear, true))
                        lstGear.Add(objCharacterGear);
                }

                // If there were no matches, don't try to merge anything.
                if (lstGear.Count == 0)
                {
                    Program.ShowMessageBox(this, await LanguageManager.GetStringAsync("Message_CannotMergeGear", token: GenericToken).ConfigureAwait(false), await LanguageManager.GetStringAsync("MessageTitle_CannotMergeGear", token: GenericToken).ConfigureAwait(false), MessageBoxButtons.OK, MessageBoxIcon.Error);
                    return;
                }

                Gear objSelectedGear;
                // Show the Select Item window.
                using (ThreadSafeForm<SelectItem> frmPickItem = await ThreadSafeForm<SelectItem>.GetAsync(() => new SelectItem(), GenericToken).ConfigureAwait(false))
                {
                    frmPickItem.MyForm.SetGearMode(lstGear);

                    if (await frmPickItem.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false) == DialogResult.Cancel)
                        return;

                    objSelectedGear = CharacterObject.Gear.DeepFindById(frmPickItem.MyForm.SelectedItem);
                }

                decimal decMinimumAmount = 1.0m;
                int intDecimalPlaces = 0;
                if (objSelectedGear.Name.StartsWith("Nuyen", StringComparison.Ordinal))
                {
                    intDecimalPlaces = Math.Max(0, CharacterObjectSettings.MaxNuyenDecimals);
                    // Need a for loop instead of a power system to maintain exact precision
                    for (int i = 0; i < intDecimalPlaces; ++i)
                        decMinimumAmount /= 10.0m;
                }
                else if (objSelectedGear.Category == "Currency")
                {
                    intDecimalPlaces = 2;
                    decMinimumAmount = 0.01m;
                }

                string strDescription = await LanguageManager.GetStringAsync("String_MergeGear", token: GenericToken).ConfigureAwait(false);
                using (ThreadSafeForm<SelectNumber> frmPickNumber = await ThreadSafeForm<SelectNumber>.GetAsync(() => new SelectNumber(intDecimalPlaces)
                       {
                           Minimum = decMinimumAmount,
                           Maximum = objGear.Quantity,
                           Description = strDescription
                       }, GenericToken).ConfigureAwait(false))
                {
                    if (await frmPickNumber.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false) == DialogResult.Cancel)
                        return;

                    // Increase the quantity for the selected item.
                    objSelectedGear.Quantity += frmPickNumber.MyForm.SelectedValue;
                    string strText = await objGear.GetCurrentDisplayNameAsync(GenericToken).ConfigureAwait(false);
                    await treGear.DoThreadSafeAsync(x =>
                    {
                        // Located the item in the Tree and update its display information.
                        TreeNode objNode = x.FindNode(objSelectedGear.InternalId);
                        if (objNode != null)
                            objNode.Text = strText;
                    }, GenericToken).ConfigureAwait(false);

                    // Reduce the quantity for the selected item.
                    objGear.Quantity -= frmPickNumber.MyForm.SelectedValue;
                }

                // If the quantity has reached 0, delete the item and any Weapons it created.
                if (objGear.Quantity <= 0)
                {
                    // Remove the Gear if its quantity has been reduced to 0.
                    await objGear.DeleteGearAsync(token: GenericToken).ConfigureAwait(false);
                }
                else
                {
                    string strText = await objGear.GetCurrentDisplayNameAsync(GenericToken).ConfigureAwait(false);
                    await treGear.DoThreadSafeAsync(() => objSelectedNode.Text = strText, GenericToken)
                                 .ConfigureAwait(false);
                    await RequestCharacterUpdate().ConfigureAwait(false);
                    await SetDirty(true).ConfigureAwait(false);
                }
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

        private async void cmdGearMoveToVehicle_Click(object sender, EventArgs e)
        {
            try
            {
                Vehicle objVehicle;
                using (ThreadSafeForm<SelectItem> frmPickItem = await ThreadSafeForm<SelectItem>.GetAsync(() => new SelectItem(), GenericToken).ConfigureAwait(false))
                {
                    frmPickItem.MyForm.SetVehiclesMode(CharacterObject.Vehicles);

                    if (await frmPickItem.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false) == DialogResult.Cancel)
                        return;

                    // Locate the selected Vehicle.
                    string strItem = frmPickItem.MyForm.SelectedItem;
                    objVehicle = await CharacterObject.Vehicles.FirstOrDefaultAsync(x => x.InternalId == strItem, GenericToken).ConfigureAwait(false);
                }

                if (objVehicle == null)
                    return;

                TreeNode objSelectedNode = await treGear.DoThreadSafeFuncAsync(x => x.SelectedNode, GenericToken).ConfigureAwait(false);
                if (!(objSelectedNode?.Tag is Gear objSelectedGear))
                    return;

                decimal decMinimumAmount = 1.0m;
                int intDecimalPlaces = 0;
                if (objSelectedGear.Name.StartsWith("Nuyen", StringComparison.Ordinal))
                {
                    intDecimalPlaces = Math.Max(0, CharacterObjectSettings.MaxNuyenDecimals);
                    // Need a for loop instead of a power system to maintain exact precision
                    for (int i = 0; i < intDecimalPlaces; ++i)
                        decMinimumAmount /= 10.0m;
                }
                else if (objSelectedGear.Category == "Currency")
                {
                    intDecimalPlaces = 2;
                    decMinimumAmount = 0.01m;
                }

                decimal decMove;
                if (objSelectedGear.Quantity == decMinimumAmount)
                    decMove = decMinimumAmount;
                else
                {
                    string strDescription = await LanguageManager.GetStringAsync("String_MoveGear").ConfigureAwait(false);
                    using (ThreadSafeForm<SelectNumber> frmPickNumber = await ThreadSafeForm<SelectNumber>.GetAsync(
                               () => new SelectNumber(intDecimalPlaces)
                               {
                                   Minimum = decMinimumAmount,
                                   Maximum = objSelectedGear.Quantity,
                                   Description = strDescription
                               }, GenericToken).ConfigureAwait(false))
                    {
                        if (await frmPickNumber.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false) == DialogResult.Cancel)
                            return;

                        decMove = frmPickNumber.MyForm.SelectedValue;
                    }
                }

                // See if the Vehicle already has a matching piece of Gear.
                Gear objFoundGear = objVehicle.GearChildren.FirstOrDefault(x => x.IsIdenticalToOtherGear(objSelectedGear));

                if (objFoundGear == null)
                {
                    // Create a new piece of Gear.
                    Gear objGear = new Gear(CharacterObject);

                    objGear.Copy(objSelectedGear);

                    objGear.Quantity = decMove;
                    objGear.Location = null;

                    objGear.Parent = objVehicle;
                    await objVehicle.GearChildren.AddAsync(objGear, GenericToken).ConfigureAwait(false);
                }
                else
                {
                    // Everything matches up, so just increase the quantity.
                    objFoundGear.Quantity += decMove;
                    string strText = await objFoundGear.GetCurrentDisplayNameAsync(GenericToken).ConfigureAwait(false);
                    await treVehicles.DoThreadSafeAsync(x =>
                    {
                        TreeNode objFoundNode = x.FindNode(objFoundGear.InternalId);
                        if (objFoundNode != null)
                            objFoundNode.Text = strText;
                    }, GenericToken).ConfigureAwait(false);
                }

                // Update the selected item.
                objSelectedGear.Quantity -= decMove;
                if (objSelectedGear.Quantity <= 0)
                {
                    await objSelectedGear.DeleteGearAsync(token: GenericToken).ConfigureAwait(false);
                }
                else
                {
                    string strText = await objSelectedGear.GetCurrentDisplayNameAsync(GenericToken).ConfigureAwait(false);
                    await treVehicles.DoThreadSafeAsync(() => objSelectedNode.Text = strText, GenericToken)
                                     .ConfigureAwait(false);
                    await RequestCharacterUpdate().ConfigureAwait(false);
                    await SetDirty(true).ConfigureAwait(false);
                }
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

        private async void cmdVehicleMoveToInventory_Click(object sender, EventArgs e)
        {
            try
            {
                TreeNode objSelectedNode = await treVehicles.DoThreadSafeFuncAsync(x => x.SelectedNode, GenericToken).ConfigureAwait(false);
                if (objSelectedNode == null)
                    return;
                switch (objSelectedNode.Tag)
                {
                    // Locate the selected Weapon.
                    case Weapon objWeapon:
                        {
                            CharacterObject.Vehicles.FindVehicleWeapon(objWeapon.InternalId, out Vehicle objVehicle, out WeaponMount objMount, out VehicleMod objMod);
                            // Move the Weapons from the Vehicle Mod (or Vehicle) to the character.
                            Weapon objParent = objWeapon.Parent;
                            if (objParent != null)
                                await objParent.Children.RemoveAsync(objWeapon).ConfigureAwait(false);
                            else if (objMount != null)
                                await objMount.Weapons.RemoveAsync(objWeapon).ConfigureAwait(false);
                            else if (objMod != null)
                                await objMod.Weapons.RemoveAsync(objWeapon).ConfigureAwait(false);
                            else
                                await objVehicle.Weapons.RemoveAsync(objWeapon).ConfigureAwait(false);

                            await CharacterObject.Weapons.AddAsync(objWeapon).ConfigureAwait(false);

                            objWeapon.ParentVehicle = null;

                            List<Gear> lstGearToMove = new List<Gear>(objWeapon.Clips.Count);
                            foreach (Clip objClip in objWeapon.Clips)
                            {
                                if (objClip.AmmoGear != null)
                                {
                                    lstGearToMove.Add(objClip.AmmoGear);
                                }
                            }
                            foreach (Clip objClip in objWeapon.Children.GetAllDescendants(x => x.Children).SelectMany(x => x.Clips))
                            {
                                if (objClip.AmmoGear != null)
                                {
                                    lstGearToMove.Add(objClip.AmmoGear);
                                }
                            }
                            foreach (Gear objGear in lstGearToMove)
                            {
                                switch (objGear.Parent)
                                {
                                    case IHasGear objHasChildren:
                                        await objHasChildren.GearChildren.RemoveAsync(objGear).ConfigureAwait(false);
                                        break;

                                    case IHasChildren<Gear> objHasChildren:
                                        await objHasChildren.Children.RemoveAsync(objGear).ConfigureAwait(false);
                                        break;

                                    default:
                                        continue;
                                }

                                await CharacterObject.Gear.AddAsync(objGear).ConfigureAwait(false);
                            }

                            break;
                        }
                    case Gear objSelectedGear:
                        {
                            // Locate the selected Gear.
                            CharacterObject.Vehicles.FindVehicleGear(objSelectedGear.InternalId);

                            decimal decMinimumAmount = 1.0m;
                            int intDecimalPlaces = 0;
                            if (objSelectedGear.Name.StartsWith("Nuyen", StringComparison.Ordinal))
                            {
                                intDecimalPlaces = Math.Max(0, CharacterObjectSettings.MaxNuyenDecimals);
                                // Need a for loop instead of a power system to maintain exact precision
                                for (int i = 0; i < intDecimalPlaces; ++i)
                                    decMinimumAmount /= 10.0m;
                            }
                            else if (objSelectedGear.Category == "Currency")
                            {
                                intDecimalPlaces = 2;
                                decMinimumAmount = 0.01m;
                            }

                            decimal decMove;
                            if (objSelectedGear.Quantity == decMinimumAmount)
                                decMove = decMinimumAmount;
                            else
                            {
                                string strDescription = await LanguageManager.GetStringAsync("String_MoveGear").ConfigureAwait(false);
                                using (ThreadSafeForm<SelectNumber> frmPickNumber
                                       = await ThreadSafeForm<SelectNumber>.GetAsync(
                                           () => new SelectNumber(intDecimalPlaces)
                                           {
                                               Minimum = decMinimumAmount,
                                               Maximum = objSelectedGear.Quantity,
                                               Description = strDescription
                                           }, GenericToken).ConfigureAwait(false))
                                {
                                    if (await frmPickNumber.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false) == DialogResult.Cancel)
                                        return;

                                    decMove = frmPickNumber.MyForm.SelectedValue;
                                }
                            }

                            // See if the character already has a matching piece of Gear.
                            Gear objFoundGear = CharacterObject.Gear.FirstOrDefault(x => objSelectedGear.IsIdenticalToOtherGear(x));

                            if (objFoundGear == null)
                            {
                                // Create a new piece of Gear.
                                Gear objGear = new Gear(CharacterObject);

                                objGear.Copy(objSelectedGear);

                                objGear.Quantity = decMove;

                                await CharacterObject.Gear.AddAsync(objGear).ConfigureAwait(false);

                                objGear.AddGearImprovements();
                            }
                            else
                            {
                                // Everything matches up, so just increase the quantity.
                                objFoundGear.Quantity += decMove;
                                string strText = await objFoundGear.GetCurrentDisplayNameAsync(token: GenericToken).ConfigureAwait(false);
                                await treGear.DoThreadSafeAsync(x =>
                                {
                                    TreeNode objFoundNode = x.FindNode(objFoundGear.InternalId);
                                    if (objFoundNode != null)
                                        objFoundNode.Text = strText;
                                }, GenericToken).ConfigureAwait(false);
                            }

                            // Update the selected item.
                            objSelectedGear.Quantity -= decMove;
                            if (objSelectedGear.Quantity <= 0)
                            {
                                await objSelectedGear.DeleteGearAsync(token: GenericToken).ConfigureAwait(false);
                            }
                            else
                            {
                                string strText = await objSelectedGear.GetCurrentDisplayNameAsync(token: GenericToken).ConfigureAwait(false);
                                await treGear.DoThreadSafeAsync(() => objSelectedNode.Text = strText).ConfigureAwait(false);
                                await RequestCharacterUpdate().ConfigureAwait(false);
                                await SetDirty(true).ConfigureAwait(false);
                            }

                            break;
                        }
                }
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

        private async void cmdGearIncreaseQty_Click(object sender, EventArgs e)
        {
            try
            {
                if (!(await treGear.DoThreadSafeFuncAsync(x => x.SelectedNode?.Tag, GenericToken).ConfigureAwait(false) is Gear objGear))
                    return;
                bool blnAddAgain;
                do
                {
                    // Select the root Gear node then open the Select Gear window.
                    blnAddAgain = await PickGear(objGear.Parent as IHasChildren<Gear>, objGear.Location, objGear, await objGear.GetCurrentDisplayNameShortAsync(GenericToken).ConfigureAwait(false), token: GenericToken).ConfigureAwait(false);
                }
                while (blnAddAgain);
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

        private async void cmdVehicleGearReduceQty_Click(object sender, EventArgs e)
        {
            try
            {
                TreeNode objSelectedNode = await treVehicles.DoThreadSafeFuncAsync(x => x.SelectedNode, GenericToken).ConfigureAwait(false);
                if (!(objSelectedNode?.Tag is Gear objGear))
                    return;
                int intDecimalPlaces = 0;
                if (objGear.Name.StartsWith("Nuyen", StringComparison.Ordinal))
                {
                    intDecimalPlaces = Math.Max(0, CharacterObjectSettings.MaxNuyenDecimals);
                }
                else if (objGear.Category == "Currency")
                {
                    intDecimalPlaces = 2;
                }

                decimal decSelectedValue;
                string strDescription = await LanguageManager.GetStringAsync("String_ReduceGear", token: GenericToken).ConfigureAwait(false);
                using (ThreadSafeForm<SelectNumber> frmPickNumber = await ThreadSafeForm<SelectNumber>.GetAsync(
                           () => new SelectNumber(intDecimalPlaces)
                           {
                               Minimum = 0,
                               Maximum = objGear.Quantity,
                               Description = strDescription
                           }, GenericToken).ConfigureAwait(false))
                {
                    if (await frmPickNumber.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false) == DialogResult.Cancel)
                        return;

                    decSelectedValue = frmPickNumber.MyForm.SelectedValue;
                }

                if (!await CommonFunctions.ConfirmDeleteAsync(string.Format(GlobalSettings.CultureInfo, await LanguageManager.GetStringAsync("Message_ReduceQty", token: GenericToken).ConfigureAwait(false), decSelectedValue.ToString(GlobalSettings.CultureInfo))).ConfigureAwait(false))
                    return;

                objGear.Quantity -= decSelectedValue;

                if (objGear.Quantity > 0)
                {
                    string strText = await objGear.GetCurrentDisplayNameAsync(GenericToken).ConfigureAwait(false);
                    await treVehicles.DoThreadSafeAsync(() => objSelectedNode.Text = strText, GenericToken).ConfigureAwait(false);
                    await RequestCharacterUpdate().ConfigureAwait(false);
                    await SetDirty(true).ConfigureAwait(false);
                }
                else
                {
                    // Remove the Gear if its quantity has been reduced to 0.
                    await objGear.DeleteGearAsync(token: GenericToken).ConfigureAwait(false);
                }
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

        private async void cmdAddQuality_Click(object sender, EventArgs e)
        {
            try
            {
                XmlDocument objXmlDocument = await CharacterObject.LoadDataAsync("qualities.xml", token: GenericToken).ConfigureAwait(false);
                bool blnAddAgain;
                do
                {
                    bool blnFreeCost;
                    XmlNode objXmlQuality;
                    int intRatingToAdd;
                    using (ThreadSafeForm<SelectQuality> frmPickQuality = await ThreadSafeForm<SelectQuality>.GetAsync(() => new SelectQuality(CharacterObject), GenericToken).ConfigureAwait(false))
                    {
                        // Don't do anything else if the form was canceled.
                        if (await frmPickQuality.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false) == DialogResult.Cancel)
                            break;
                        blnAddAgain = frmPickQuality.MyForm.AddAgain;
                        blnFreeCost = frmPickQuality.MyForm.FreeCost;

                        objXmlQuality = objXmlDocument.SelectSingleNode("/chummer/qualities/quality[id = " + frmPickQuality.MyForm.SelectedQuality.CleanXPath() + ']');
                        intRatingToAdd = frmPickQuality.MyForm.SelectedRating;
                        int intDummy = 0;
                        if (objXmlQuality != null && objXmlQuality["nolevels"] == null && objXmlQuality.TryGetInt32FieldQuickly("limit", ref intDummy))
                        {
                            string strQuality = frmPickQuality.MyForm.SelectedQuality;
                            intRatingToAdd -= await CharacterObject.Qualities.CountAsync(x =>
                                x.SourceIDString.Equals(strQuality,
                                                        StringComparison.OrdinalIgnoreCase)
                                && string.IsNullOrEmpty(x.SourceName), GenericToken).ConfigureAwait(false);
                        }
                    }

                    if (objXmlQuality == null)
                        continue;

                    QualityType eQualityType = QualityType.Positive;
                    string strTemp = string.Empty;
                    if (objXmlQuality.TryGetStringFieldQuickly("category", ref strTemp))
                        eQualityType = Quality.ConvertToQualityType(strTemp);

                    // Helps to capture a write lock here for performance purposes
                    IAsyncDisposable objLocker = await CharacterObject.LockObject.EnterWriteLockAsync(GenericToken)
                                                                      .ConfigureAwait(false);
                    try
                    {
                        for (int i = 1; i <= intRatingToAdd; ++i)
                        {
                            // Positive Metagenetic Qualities are free if you're a Changeling.
                            if (CharacterObject.MetagenicLimit > 0
                                && objXmlQuality["metagenic"]?.InnerText == bool.TrueString)
                                blnFreeCost = true;
                            // The Beast's Way and the Spiritual Way get the Mentor Spirit for free.
                            else if (objXmlQuality["name"]?.InnerText == "Mentor Spirit" &&
                                     await CharacterObject.Qualities.AnyAsync(x =>
                                                                                  x.Name == "The Beast's Way"
                                                                                  || x.Name == "The Spiritual Way",
                                                                              GenericToken).ConfigureAwait(false))
                                blnFreeCost = true;

                            int intQualityBP = 0;
                            if (!blnFreeCost)
                            {
                                objXmlQuality.TryGetInt32FieldQuickly("karma", ref intQualityBP);
                                XmlNode xmlDiscountNode = objXmlQuality["costdiscount"];
                                if (xmlDiscountNode?.CreateNavigator().RequirementsMet(CharacterObject) == true)
                                {
                                    int intTemp = 0;
                                    xmlDiscountNode.TryGetInt32FieldQuickly("value", ref intTemp);
                                    switch (eQualityType)
                                    {
                                        case QualityType.Positive:
                                            intQualityBP += intTemp;
                                            break;

                                        case QualityType.Negative:
                                            intQualityBP -= intTemp;
                                            break;
                                    }
                                }
                            }

                            int intKarmaCost = intQualityBP * CharacterObjectSettings.KarmaQuality;
                            if (!CharacterObjectSettings.DontDoubleQualityPurchases &&
                                objXmlQuality["doublecareer"]?.InnerText != bool.FalseString)
                                intKarmaCost *= 2;

                            // Make sure the character has enough Karma to pay for the Quality.
                            if (eQualityType == QualityType.Positive)
                            {
                                if (!blnFreeCost)
                                {
                                    if (intKarmaCost > await CharacterObject.GetKarmaAsync(GenericToken)
                                                                            .ConfigureAwait(false) &&
                                        objXmlQuality["stagedpurchase"]?.InnerText != bool.TrueString)
                                    {
                                        Program.ShowMessageBox(this,
                                                               await LanguageManager
                                                                     .GetStringAsync("Message_NotEnoughKarma")
                                                                     .ConfigureAwait(false),
                                                               await LanguageManager
                                                                     .GetStringAsync("MessageTitle_NotEnoughKarma")
                                                                     .ConfigureAwait(false), MessageBoxButtons.OK,
                                                               MessageBoxIcon.Information);
                                        break;
                                    }

                                    string strDisplayName = objXmlQuality["translate"]?.InnerText ??
                                                            objXmlQuality["name"]?.InnerText ??
                                                            await LanguageManager.GetStringAsync("String_Unknown")
                                                                .ConfigureAwait(false);
                                    if (!await CommonFunctions.ConfirmKarmaExpenseAsync(
                                                                  string.Format(GlobalSettings.CultureInfo,
                                                                      await LanguageManager
                                                                            .GetStringAsync(
                                                                                "Message_ConfirmKarmaExpenseSpend")
                                                                            .ConfigureAwait(false), strDisplayName,
                                                                      intKarmaCost.ToString(GlobalSettings
                                                                          .CultureInfo)))
                                                              .ConfigureAwait(false))
                                        break;
                                }
                            }
                            else if (Program.ShowMessageBox(this,
                                                            await LanguageManager
                                                                  .GetStringAsync("Message_AddNegativeQuality")
                                                                  .ConfigureAwait(false),
                                                            await LanguageManager
                                                                  .GetStringAsync("MessageTitle_AddNegativeQuality")
                                                                  .ConfigureAwait(false), MessageBoxButtons.YesNo,
                                                            MessageBoxIcon.Question) == DialogResult.No)
                                break;

                            List<Weapon> lstWeapons = new List<Weapon>(1);
                            Quality objQuality = new Quality(CharacterObject);
                            try
                            {
                                objQuality.Create(objXmlQuality, QualitySource.Selected, lstWeapons);
                                if (objQuality.InternalId.IsEmptyGuid())
                                {
                                    // If the Quality could not be added, remove the Improvements that were added during the Quality Creation process.
                                    await ImprovementManager.RemoveImprovementsAsync(
                                        CharacterObject, Improvement.ImprovementSource.Quality,
                                        objQuality.InternalId).ConfigureAwait(false);
                                    await objQuality.DisposeAsync().ConfigureAwait(false);
                                    break;
                                }

                                // Make sure the character has enough Karma to pay for the Quality.
                                if (objQuality.Type == QualityType.Positive)
                                {
                                    if (objQuality.ContributeToBP)
                                    {
                                        // Create the Karma expense.
                                        ExpenseLogEntry objExpense = new ExpenseLogEntry(CharacterObject);
                                        objExpense.Create(intKarmaCost * -1,
                                                          await LanguageManager
                                                                .GetStringAsync(
                                                                    "String_ExpenseAddPositiveQuality",
                                                                    token: GenericToken)
                                                                .ConfigureAwait(false) +
                                                          await LanguageManager
                                                                .GetStringAsync("String_Space", token: GenericToken)
                                                                .ConfigureAwait(false) +
                                                          await objQuality.GetCurrentDisplayNameShortAsync(GenericToken)
                                                                          .ConfigureAwait(false), ExpenseType.Karma,
                                                          DateTime.Now);
                                        await CharacterObject.ExpenseEntries
                                                             .AddWithSortAsync(objExpense, token: GenericToken)
                                                             .ConfigureAwait(false);
                                        await CharacterObject.ModifyKarmaAsync(-intKarmaCost, GenericToken)
                                                             .ConfigureAwait(false);

                                        ExpenseUndo objUndo = new ExpenseUndo();
                                        objUndo.CreateKarma(KarmaExpenseType.AddQuality, objQuality.InternalId);
                                        objExpense.Undo = objUndo;
                                    }
                                }
                                else
                                {
                                    // Create a Karma Expense for the Negative Quality.
                                    ExpenseLogEntry objExpense = new ExpenseLogEntry(CharacterObject);
                                    objExpense.Create(0,
                                                      await LanguageManager
                                                            .GetStringAsync("String_ExpenseAddNegativeQuality",
                                                                            token: GenericToken).ConfigureAwait(false) +
                                                      await LanguageManager
                                                            .GetStringAsync("String_Space", token: GenericToken)
                                                            .ConfigureAwait(false) +
                                                      await objQuality.GetCurrentDisplayNameShortAsync(GenericToken)
                                                                      .ConfigureAwait(false), ExpenseType.Karma,
                                                      DateTime.Now);
                                    await CharacterObject
                                          .ExpenseEntries.AddWithSortAsync(objExpense, token: GenericToken)
                                          .ConfigureAwait(false);

                                    ExpenseUndo objUndo = new ExpenseUndo();
                                    objUndo.CreateKarma(KarmaExpenseType.AddQuality, objQuality.InternalId);
                                    objExpense.Undo = objUndo;
                                }
                            }
                            catch
                            {
                                await objQuality.DisposeAsync().ConfigureAwait(false);
                                throw;
                            }

                            await CharacterObject.Qualities.AddAsync(objQuality, GenericToken).ConfigureAwait(false);

                            // Add any created Weapons to the character.
                            foreach (Weapon objWeapon in lstWeapons)
                            {
                                await CharacterObject.Weapons.AddAsync(objWeapon, GenericToken).ConfigureAwait(false);
                            }
                        }
                    }
                    finally
                    {
                        await objLocker.DisposeAsync().ConfigureAwait(false);
                    }
                }
                while (blnAddAgain);
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

        private async void cmdDeleteQuality_Click(object sender, EventArgs e)
        {
            try
            {
                await DoDeleteQuality(GenericToken).ConfigureAwait(false);
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

        private async ValueTask DoDeleteQuality(CancellationToken token = default)
        {
            // Locate the selected Quality.
            if (!(await treQualities.DoThreadSafeFuncAsync(x => x.SelectedNode?.Tag, token).ConfigureAwait(false) is Quality objSelectedQuality))
                return;
            string strInternalIDToRemove = objSelectedQuality.InternalId;
            // Can't do a foreach because we're removing items, this is the next best thing
            Quality objQualityToRemove =
                CharacterObject.Qualities.LastOrDefault(x => x.InternalId == strInternalIDToRemove);
            await RemoveQuality(objQualityToRemove, token: token).ConfigureAwait(false);
        }

        private async void cmdSwapQuality_Click(object sender, EventArgs e)
        {
            try
            {
                // Locate the selected Quality.
                Quality objQuality = await treQualities.DoThreadSafeFuncAsync(x => x.SelectedNode?.Tag, GenericToken).ConfigureAwait(false) as Quality;
                if (objQuality?.InternalId.IsEmptyGuid() != false)
                    return;

                switch (objQuality.OriginSource)
                {
                    // Qualities that come from a Metatype cannot be removed.
                    case QualitySource.Metatype:
                        Program.ShowMessageBox(this, await LanguageManager.GetStringAsync("Message_MetavariantQualitySwap").ConfigureAwait(false),
                            await LanguageManager.GetStringAsync("MessageTitle_MetavariantQualitySwap").ConfigureAwait(false), MessageBoxButtons.OK, MessageBoxIcon.Information);
                        return;
                    // Neither can qualities from Improvements
                    case QualitySource.Improvement:
                        Program.ShowMessageBox(this, string.Format(GlobalSettings.CultureInfo, await LanguageManager.GetStringAsync("Message_ImprovementQuality").ConfigureAwait(false), await objQuality.GetSourceNameAsync(GlobalSettings.Language).ConfigureAwait(false)),
                            await LanguageManager.GetStringAsync("MessageTitle_MetavariantQuality").ConfigureAwait(false), MessageBoxButtons.OK, MessageBoxIcon.Information);
                        return;
                }

                XmlNode objXmlQuality;
                int intRatingToAdd;
                using (ThreadSafeForm<SelectQuality> frmPickQuality = await ThreadSafeForm<SelectQuality>.GetAsync(
                           () => new SelectQuality(CharacterObject)
                           {
                               ForceCategory = objQuality.Type.ToString(),
                               IgnoreQuality = objQuality.Name
                           }, GenericToken).ConfigureAwait(false))
                {
                    // Don't do anything else if the form was canceled.
                    if (await frmPickQuality.ShowDialogSafeAsync(this, GenericToken).ConfigureAwait(false) == DialogResult.Cancel)
                        return;

                    objXmlQuality = (await CharacterObject.LoadDataAsync("qualities.xml", token: GenericToken).ConfigureAwait(false)).SelectSingleNode("/chummer/qualities/quality[id = " + frmPickQuality.MyForm.SelectedQuality.CleanXPath() + ']');
                    intRatingToAdd = frmPickQuality.MyForm.SelectedRating;
                    int intDummy = 0;
                    if (objXmlQuality != null && objXmlQuality["nolevels"] == null && objXmlQuality.TryGetInt32FieldQuickly("limit", ref intDummy))
                    {
                        string strQuality = frmPickQuality.MyForm.SelectedQuality;
                        intRatingToAdd -= await CharacterObject.Qualities.CountAsync(x =>
                            x.SourceIDString.Equals(strQuality,
                                                    StringComparison.OrdinalIgnoreCase) && string.IsNullOrEmpty(x.SourceName), GenericToken).ConfigureAwait(false);
                    }
                }

                if (intRatingToAdd > 0)
                {
                    Quality objNewQuality = new Quality(CharacterObject);
                    try
                    {
                        await objNewQuality.Swap(objQuality, objXmlQuality, intRatingToAdd, GenericToken)
                                           .ConfigureAwait(false);
                    }
                    catch
                    {
                        await objNewQuality.DisposeAsync().ConfigureAwait(false);
                        throw;
                    }
                }
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

        private async ValueTask<bool> RemoveQuality(Quality objSelectedQuality, bool blnConfirmDelete = true,
                                                    bool blnCompleteDelete = true, CancellationToken token = default)
        {
            using (await EnterReadLock.EnterAsync(objSelectedQuality.LockObject, token).ConfigureAwait(false))
            {
                XPathNavigator objXmlDeleteQuality
                    = await objSelectedQuality.GetNodeXPathAsync(token).ConfigureAwait(false);
                bool blnMetatypeQuality = false;

                switch (objSelectedQuality.OriginSource)
                {
                    // Qualities that come from a Metatype cannot be removed.
                    case QualitySource.Metatype:
                        Program.ShowMessageBox(
                            this,
                            await LanguageManager.GetStringAsync("Message_MetavariantQuality", token: token)
                                                 .ConfigureAwait(false),
                            await LanguageManager.GetStringAsync("MessageTitle_MetavariantQuality", token: token)
                                                 .ConfigureAwait(false), MessageBoxButtons.OK,
                            MessageBoxIcon.Information);
                        return false;

                    case QualitySource.Improvement:
                        Program.ShowMessageBox(
                            this,
                            string.Format(GlobalSettings.CultureInfo,
                                          await LanguageManager
                                                .GetStringAsync("Message_ImprovementQuality", token: token)
                                                .ConfigureAwait(false),
                                          await objSelectedQuality.GetSourceNameAsync(GlobalSettings.Language, token)
                                                                  .ConfigureAwait(false)),
                            await LanguageManager.GetStringAsync("MessageTitle_MetavariantQuality", token: token)
                                                 .ConfigureAwait(false), MessageBoxButtons.OK,
                            MessageBoxIcon.Information);
                        return false;

                    case QualitySource.MetatypeRemovable:
                    {
                        // Look up the cost of the Quality.
                        int intBP = 0;
                        if (objSelectedQuality.Type == QualityType.Negative || await objXmlDeleteQuality
                                .SelectSingleNodeAndCacheExpressionAsync("refundkarmaonremove", token: token)
                                .ConfigureAwait(false) != null)
                        {
                            intBP = Convert.ToInt32(
                                (await objXmlDeleteQuality
                                       .SelectSingleNodeAndCacheExpressionAsync("karma", token: token)
                                       .ConfigureAwait(false))?.Value,
                                GlobalSettings.InvariantCultureInfo) * CharacterObjectSettings.KarmaQuality;
                            if (blnCompleteDelete)
                                intBP *= objSelectedQuality.Levels;
                            if (!CharacterObjectSettings.DontDoubleQualityPurchases && objSelectedQuality.DoubleCost)
                            {
                                intBP *= 2;
                            }

                            if (objSelectedQuality.Type == QualityType.Positive)
                                intBP *= -1;
                        }

                        string strBP = intBP.ToString(GlobalSettings.CultureInfo)
                                       + await LanguageManager.GetStringAsync("String_Space", token: token)
                                                              .ConfigureAwait(false) + await LanguageManager
                                           .GetStringAsync("String_Karma", token: token).ConfigureAwait(false);

                        if (blnConfirmDelete && !await CommonFunctions.ConfirmDeleteAsync(
                                string.Format(GlobalSettings.CultureInfo,
                                              await LanguageManager
                                                    .GetStringAsync(
                                                        blnCompleteDelete
                                                            ? "Message_DeleteMetatypeQuality"
                                                            : "Message_LowerMetatypeQualityLevel", token: token)
                                                    .ConfigureAwait(false), strBP), token).ConfigureAwait(false))
                            return false;

                        blnMetatypeQuality = true;
                        break;
                    }
                }

                // Helps to capture a write lock here for performance purposes
                IAsyncDisposable objLocker = await CharacterObject.LockObject.EnterWriteLockAsync(token)
                                                                  .ConfigureAwait(false);
                try
                {
                    if (objSelectedQuality.Type == QualityType.Positive)
                    {
                        if (await objXmlDeleteQuality
                                  .SelectSingleNodeAndCacheExpressionAsync("refundkarmaonremove", token: token)
                                  .ConfigureAwait(false) != null)
                        {
                            int intKarmaCost = objSelectedQuality.BP * CharacterObjectSettings.KarmaQuality;

                            if (!CharacterObjectSettings.DontDoubleQualityPurchases && objSelectedQuality.DoubleCost)
                            {
                                intKarmaCost *= 2;
                            }

                            ExpenseLogEntry objExpense = new ExpenseLogEntry(CharacterObject);
                            objExpense.Create(intKarmaCost, string.Format(GlobalSettings.CultureInfo,
                                                                          await LanguageManager
                                                                              .GetStringAsync(
                                                                                  "String_ExpenseSwapPositiveQuality",
                                                                                  token: token).ConfigureAwait(false)
                                                                          , await objSelectedQuality
                                                                              .GetCurrentDisplayNameShortAsync(token)
                                                                              .ConfigureAwait(false)
                                                                          , await LanguageManager.GetStringAsync("String_Karma", token: token).ConfigureAwait(false)),
                                              ExpenseType.Karma, DateTime.Now, true);
                            await CharacterObject.ExpenseEntries.AddWithSortAsync(objExpense, token: token)
                                                 .ConfigureAwait(false);
                            await CharacterObject.ModifyKarmaAsync(intKarmaCost, token).ConfigureAwait(false);

                            ExpenseUndo objUndo = new ExpenseUndo();
                            objUndo.CreateKarma(KarmaExpenseType.RemoveQuality, objSelectedQuality.SourceIDString);
                            objUndo.Extra = objSelectedQuality.Extra;
                            objExpense.Undo = objUndo;
                        }
                        else if (!blnMetatypeQuality && blnConfirmDelete && !await CommonFunctions.ConfirmDeleteAsync(
                                     blnCompleteDelete
                                         ? await LanguageManager
                                                 .GetStringAsync("Message_DeletePositiveQualityCareer", token: token)
                                                 .ConfigureAwait(false)
                                         : await LanguageManager
                                                 .GetStringAsync("Message_LowerPositiveQualityLevelCareer",
                                                                 token: token)
                                                 .ConfigureAwait(false), token).ConfigureAwait(false))
                            return false;
                    }
                    else
                    {
                        // Make sure the character has enough Karma to buy off the Quality.
                        int intKarmaCost = -(objSelectedQuality.BP * CharacterObjectSettings.KarmaQuality);
                        if (!CharacterObjectSettings.DontDoubleQualityRefunds)
                        {
                            intKarmaCost *= 2;
                        }

                        int intTotalKarmaCost = intKarmaCost;
                        if (blnCompleteDelete)
                            intTotalKarmaCost *= objSelectedQuality.Levels;
                        if (intTotalKarmaCost > await CharacterObject.GetKarmaAsync(token).ConfigureAwait(false))
                        {
                            Program.ShowMessageBox(
                                this,
                                await LanguageManager.GetStringAsync("Message_NotEnoughKarma", token: token)
                                                     .ConfigureAwait(false),
                                await LanguageManager.GetStringAsync("MessageTitle_NotEnoughKarma", token: token)
                                                     .ConfigureAwait(false), MessageBoxButtons.OK,
                                MessageBoxIcon.Information);
                            return false;
                        }

                        if (!blnMetatypeQuality && blnConfirmDelete && !await CommonFunctions.ConfirmKarmaExpenseAsync(
                                    string.Format(GlobalSettings.CultureInfo, blnCompleteDelete
                                                      ? await LanguageManager
                                                              .GetStringAsync(
                                                                  "Message_ConfirmKarmaExpenseRemove", token: token)
                                                              .ConfigureAwait(false)
                                                      : await LanguageManager
                                                              .GetStringAsync("Message_ConfirmKarmaExpenseLowerLevel",
                                                                              token: token).ConfigureAwait(false),
                                                  await objSelectedQuality.GetCurrentDisplayNameShortAsync(token)
                                                                          .ConfigureAwait(false), intTotalKarmaCost),
                                    token)
                                .ConfigureAwait(false))
                            return false;

                        // Create the Karma expense.
                        ExpenseLogEntry objExpense = new ExpenseLogEntry(CharacterObject);
                        objExpense.Create(-intTotalKarmaCost,
                                          await LanguageManager
                                                .GetStringAsync("String_ExpenseRemoveNegativeQuality", token: token)
                                                .ConfigureAwait(false)
                                          + await LanguageManager.GetStringAsync("String_Space", token: token)
                                                                 .ConfigureAwait(false)
                                          + await objSelectedQuality.GetCurrentDisplayNameShortAsync(token)
                                                                    .ConfigureAwait(false), ExpenseType.Karma,
                                          DateTime.Now);
                        await CharacterObject.ExpenseEntries.AddWithSortAsync(objExpense, token: token)
                                             .ConfigureAwait(false);
                        await CharacterObject.ModifyKarmaAsync(-intTotalKarmaCost, token).ConfigureAwait(false);

                        ExpenseUndo objUndo = new ExpenseUndo();
                        objUndo.CreateKarma(KarmaExpenseType.RemoveQuality, objSelectedQuality.SourceIDString);
                        objUndo.Extra = objSelectedQuality.Extra;
                        objExpense.Undo = objUndo;
                    }

                    // Remove any Critter Powers that are gained through the Quality (Infected).
                    if (await objXmlDeleteQuality.SelectSingleNodeAndCacheExpressionAsync("powers/power", token: token)
                                                 .ConfigureAwait(false) != null)
                    {
                        foreach (XPathNavigator objXmlPower in await (await CharacterObject
                                                                            .LoadDataXPathAsync(
                                                                                "critterpowers.xml", token: token)
                                                                            .ConfigureAwait(false))
                                                                     .SelectAndCacheExpressionAsync(
                                                                         "optionalpowers/optionalpower", token: token)
                                                                     .ConfigureAwait(false))
                        {
                            string strExtra = (await objXmlPower
                                                     .SelectSingleNodeAndCacheExpressionAsync("@select", token: token)
                                                     .ConfigureAwait(false))?.Value;

                            foreach (CritterPower objPower in CharacterObject.CritterPowers)
                            {
                                if (objPower.Name != objXmlPower.Value || objPower.Extra != strExtra)
                                    continue;
                                // Remove any Improvements created by the Critter Power.
                                await ImprovementManager
                                      .RemoveImprovementsAsync(CharacterObject,
                                                               Improvement.ImprovementSource.CritterPower,
                                                               objPower.InternalId, token).ConfigureAwait(false);

                                // Remove the Critter Power from the character.
                                await CharacterObject.CritterPowers.RemoveAsync(objPower, token).ConfigureAwait(false);
                                break;
                            }
                        }
                    }

                    // Fix for legacy characters with old addqualities improvements.
                    await RemoveAddedQualities(
                        await objXmlDeleteQuality.SelectAndCacheExpressionAsync("addqualities/addquality", token)
                                                 .ConfigureAwait(false), token).ConfigureAwait(false);
                }
                finally
                {
                    await objLocker.DisposeAsync().ConfigureAwait(false);
                }
            }

            // Perform removal
            await objSelectedQuality.DeleteQualityAsync(blnCompleteDelete, token).ConfigureAwait(false);

            return true;
        }

        private async ValueTask UpdateQualityLevelValue(Quality objSelectedQuality = null, CancellationToken token = default)
        {
            token.ThrowIfCancellationRequested();
            if (objSelectedQuality == null)
            {
                await nudQualityLevel.DoThreadSafeAsync(x =>
                {
                    x.Value = 1;
                    x.Enabled = false;
                }, token).ConfigureAwait(false);
                return;
            }

            using (await EnterReadLock.EnterAsync(objSelectedQuality.LockObject, token).ConfigureAwait(false))
            {
                if (objSelectedQuality.OriginSource == QualitySource.Improvement
                    || objSelectedQuality.OriginSource == QualitySource.Metatype
                    || objSelectedQuality.Levels == 0)
                {
                    await nudQualityLevel.DoThreadSafeAsync(x =>
                    {
                        x.Value = 1;
                        x.Enabled = false;
                    }, token).ConfigureAwait(false);
                    return;
                }

                token.ThrowIfCancellationRequested();
                XPathNavigator objQualityNode = await objSelectedQuality.GetNodeXPathAsync(token).ConfigureAwait(false);
                string strLimitString = objQualityNode != null
                    ? (await objQualityNode.SelectSingleNodeAndCacheExpressionAsync("limit", token: token)
                                           .ConfigureAwait(false))?.Value ?? string.Empty
                    : string.Empty;
                token.ThrowIfCancellationRequested();
                if (!string.IsNullOrWhiteSpace(strLimitString)
                    && await objQualityNode.SelectSingleNodeAndCacheExpressionAsync("nolevels", token: token)
                                           .ConfigureAwait(false) == null
                    && int.TryParse(strLimitString, out int intMaxRating))
                {
                    await nudQualityLevel.DoThreadSafeAsync(x =>
                    {
                        x.Maximum = intMaxRating;
                        x.Value = objSelectedQuality.Levels;
                        x.Enabled = true;
                    }, token).ConfigureAwait(false);
                }
                else
                {
                    await nudQualityLevel.DoThreadSafeAsync(x =>
                    {
                        x.Value = 1;
                        x.Enabled = false;
                    }, token).ConfigureAwait(false);
                }
            }
        }

        private async void nudQualityLevel_ValueChanged(object sender, EventArgs e)
        {
            try
            {
                // Locate the selected Quality.
                if (!(await treQualities.DoThreadSafeFuncAsync(x => x.SelectedNode?.Tag, GenericToken).ConfigureAwait(false) is Quality
                        objSelectedQuality))
                    return;

                bool blnDoRemoveQuality = false;
                using (await EnterReadLock.EnterAsync(objSelectedQuality.LockObject, GenericToken).ConfigureAwait(false))
                {
                    int intCurrentLevels = objSelectedQuality.Levels;
                    int intSelectedLevels
                        = await nudQualityLevel.DoThreadSafeFuncAsync(x => x.ValueAsInt, GenericToken)
                                               .ConfigureAwait(false);

                    // Helps to capture a write lock here for performance purposes
                    IAsyncDisposable objLocker = await CharacterObject.LockObject.EnterWriteLockAsync(GenericToken)
                                                                      .ConfigureAwait(false);
                    try
                    {
                        // Adding a new level
                        for (; intSelectedLevels > intCurrentLevels; ++intCurrentLevels)
                        {
                            XPathNavigator objXmlSelectedQuality
                                = await objSelectedQuality.GetNodeXPathAsync(GenericToken).ConfigureAwait(false);
                            if (!objXmlSelectedQuality.RequirementsMet(CharacterObject,
                                                                       await LanguageManager.GetStringAsync(
                                                                               "String_Quality", token: GenericToken)
                                                                           .ConfigureAwait(false)))
                            {
                                await UpdateQualityLevelValue(objSelectedQuality, GenericToken).ConfigureAwait(false);
                                break;
                            }

                            bool blnFreeCost = objSelectedQuality.BP == 0 || !objSelectedQuality.ContributeToBP;

                            QualityType eQualityType = objSelectedQuality.Type;

                            int intQualityBP = 0;
                            if (!blnFreeCost)
                            {
                                objXmlSelectedQuality.TryGetInt32FieldQuickly("karma", ref intQualityBP);
                                XPathNavigator xpnDiscountNode
                                    = await objXmlSelectedQuality
                                            .SelectSingleNodeAndCacheExpressionAsync("costdiscount")
                                            .ConfigureAwait(false);
                                if (xpnDiscountNode?.RequirementsMet(CharacterObject) == true)
                                {
                                    int intTemp = 0;
                                    xpnDiscountNode.TryGetInt32FieldQuickly("value", ref intTemp);
                                    switch (eQualityType)
                                    {
                                        case QualityType.Positive:
                                            intQualityBP += intTemp;
                                            break;

                                        case QualityType.Negative:
                                            intQualityBP -= intTemp;
                                            break;
                                    }
                                }
                            }

                            int intKarmaCost = intQualityBP * CharacterObjectSettings.KarmaQuality;
                            if (!CharacterObjectSettings.DontDoubleQualityPurchases && objSelectedQuality.DoubleCost)
                                intKarmaCost *= 2;

                            // Make sure the character has enough Karma to pay for the Quality.
                            if (eQualityType == QualityType.Positive)
                            {
                                if (!blnFreeCost)
                                {
                                    if (intKarmaCost
                                        > await CharacterObject.GetKarmaAsync(GenericToken).ConfigureAwait(false)
                                        && !objSelectedQuality.StagedPurchase)
                                    {
                                        Program.ShowMessageBox(
                                            this,
                                            await LanguageManager
                                                  .GetStringAsync("Message_NotEnoughKarma", token: GenericToken)
                                                  .ConfigureAwait(false),
                                            await LanguageManager.GetStringAsync(
                                                                     "MessageTitle_NotEnoughKarma", token: GenericToken)
                                                                 .ConfigureAwait(false),
                                            MessageBoxButtons.OK,
                                            MessageBoxIcon.Information);
                                        await UpdateQualityLevelValue(objSelectedQuality, GenericToken)
                                            .ConfigureAwait(false);
                                        break;
                                    }

                                    string strDisplayName
                                        = (await objXmlSelectedQuality
                                                 .SelectSingleNodeAndCacheExpressionAsync("translate")
                                                 .ConfigureAwait(false))
                                          ?.Value
                                          ?? (await objXmlSelectedQuality
                                                    .SelectSingleNodeAndCacheExpressionAsync("name")
                                                    .ConfigureAwait(false))
                                          ?.Value
                                          ?? await LanguageManager.GetStringAsync("String_Unknown", token: GenericToken)
                                                                  .ConfigureAwait(false);
                                    if (!await CommonFunctions.ConfirmKarmaExpenseAsync(
                                                                  string.Format(GlobalSettings.CultureInfo,
                                                                      await LanguageManager.GetStringAsync(
                                                                              "Message_ConfirmKarmaExpenseSpend",
                                                                              token: GenericToken)
                                                                          .ConfigureAwait(false)
                                                                      , strDisplayName
                                                                      , intKarmaCost.ToString(GlobalSettings.CultureInfo)))
                                                              .ConfigureAwait(false))
                                    {
                                        await UpdateQualityLevelValue(objSelectedQuality, GenericToken)
                                            .ConfigureAwait(false);
                                        break;
                                    }
                                }
                            }
                            else if (Program.ShowMessageBox(
                                         this,
                                         await LanguageManager.GetStringAsync("Message_AddNegativeQuality",
                                                                              token: GenericToken)
                                                              .ConfigureAwait(false),
                                         await LanguageManager.GetStringAsync("MessageTitle_AddNegativeQuality",
                                                                              token: GenericToken)
                                                              .ConfigureAwait(false),
                                         MessageBoxButtons.YesNo, MessageBoxIcon.Question) == DialogResult.No)
                            {
                                await UpdateQualityLevelValue(objSelectedQuality, GenericToken).ConfigureAwait(false);
                                break;
                            }

                            List<Weapon> lstWeapons = new List<Weapon>(1);
                            Quality objQuality = new Quality(CharacterObject);
                            try
                            {
                                objQuality.Create(
                                    await objSelectedQuality.GetNodeAsync(GenericToken).ConfigureAwait(false),
                                    QualitySource.Selected,
                                    lstWeapons, objSelectedQuality.Extra);
                                if (objQuality.InternalId.IsEmptyGuid())
                                {
                                    // If the Quality could not be added, remove the Improvements that were added during the Quality Creation process.
                                    await ImprovementManager.RemoveImprovementsAsync(
                                                                CharacterObject, Improvement.ImprovementSource.Quality,
                                                                objQuality.InternalId)
                                                            .ConfigureAwait(false);
                                    await objQuality.DisposeAsync().ConfigureAwait(false);
                                    await UpdateQualityLevelValue(objSelectedQuality, GenericToken)
                                        .ConfigureAwait(false);
                                    break;
                                }

                                objQuality.BP = objSelectedQuality.BP;
                                objQuality.ContributeToLimit = objSelectedQuality.ContributeToLimit;

                                // Make sure the character has enough Karma to pay for the Quality.
                                if (objQuality.Type == QualityType.Positive)
                                {
                                    // Create the Karma expense.
                                    ExpenseLogEntry objExpense = new ExpenseLogEntry(CharacterObject);
                                    objExpense.Create(intKarmaCost * -1,
                                                      await LanguageManager.GetStringAsync(
                                                                               "String_ExpenseAddPositiveQuality",
                                                                               token: GenericToken)
                                                                           .ConfigureAwait(false)
                                                      + await LanguageManager
                                                              .GetStringAsync("String_Space", token: GenericToken)
                                                              .ConfigureAwait(false)
                                                      + await objQuality.GetCurrentDisplayNameShortAsync(GenericToken)
                                                                        .ConfigureAwait(false), ExpenseType.Karma,
                                                      DateTime.Now);
                                    await CharacterObject
                                          .ExpenseEntries.AddWithSortAsync(objExpense, token: GenericToken)
                                          .ConfigureAwait(false);
                                    await CharacterObject.ModifyKarmaAsync(-intKarmaCost, GenericToken)
                                                         .ConfigureAwait(false);

                                    ExpenseUndo objUndo = new ExpenseUndo();
                                    objUndo.CreateKarma(KarmaExpenseType.AddQuality, objQuality.InternalId);
                                    objExpense.Undo = objUndo;
                                }
                                else
                                {
                                    // Create a Karma Expense for the Negative Quality.
                                    ExpenseLogEntry objExpense = new ExpenseLogEntry(CharacterObject);
                                    objExpense.Create(
                                        0,
                                        await LanguageManager.GetStringAsync("String_ExpenseAddNegativeQuality",
                                                                             token: GenericToken).ConfigureAwait(false)
                                        + await LanguageManager.GetStringAsync("String_Space", token: GenericToken)
                                                               .ConfigureAwait(false)
                                        + await objQuality.GetCurrentDisplayNameShortAsync(GenericToken)
                                                          .ConfigureAwait(false),
                                        ExpenseType.Karma, DateTime.Now);
                                    await CharacterObject.ExpenseEntries.AddWithSortAsync(objExpense)
                                                         .ConfigureAwait(false);

                                    ExpenseUndo objUndo = new ExpenseUndo();
                                    objUndo.CreateKarma(KarmaExpenseType.AddQuality, objQuality.InternalId);
                                    objExpense.Undo = objUndo;
                                }
                            }
                            catch
                            {
                                await objQuality.DisposeAsync().ConfigureAwait(false);
                                throw;
                            }

                            // Add the Quality to the appropriate parent node.
                            await CharacterObject.Qualities.AddAsync(objQuality, GenericToken).ConfigureAwait(false);

                            // Add any created Weapons to the character.
                            foreach (Weapon objWeapon in lstWeapons)
                            {
                                await CharacterObject.Weapons.AddAsync(objWeapon, GenericToken).ConfigureAwait(false);
                            }
                        }

                        // Removing a level
                        for (; intSelectedLevels < intCurrentLevels; --intCurrentLevels)
                        {
                            Quality objInvisibleQuality = CharacterObject.Qualities.FirstOrDefault(
                                x => x.SourceIDString == objSelectedQuality.SourceIDString
                                     && x.Extra == objSelectedQuality.Extra
                                     && x.SourceName == objSelectedQuality.SourceName
                                     && x.InternalId != objSelectedQuality.InternalId
                                     && !ReferenceEquals(x, objSelectedQuality));
                            if (objInvisibleQuality == null
                                || !await RemoveQuality(objInvisibleQuality, false, false, GenericToken)
                                    .ConfigureAwait(false))
                            {
                                blnDoRemoveQuality = true;
                                break;
                            }
                        }
                    }
                    finally
                    {
                        await objLocker.DisposeAsync().ConfigureAwait(false);
                    }
                }

                if (blnDoRemoveQuality)
                {
                    if (!await RemoveQuality(objSelectedQuality, false, false, GenericToken)
                            .ConfigureAwait(false))
                    {
                        await UpdateQualityLevelValue(objSelectedQuality, GenericToken)
                            .ConfigureAwait(false);
                    }
                }
            }
            catch (OperationCanceledException)
            {
                // swallow this
            }
        }

