                if (string.IsNullOrEmpty(strSelectedId))
                    return;

                bool blnAddAgain;
                do
                {
                    blnAddAgain = await PickArmorGear(strSelectedId, token: GenericToken).ConfigureAwait(false);
                } while (blnAddAgain);
            }
            catch (OperationCanceledException)
            {
                //swallow this
            }
        }

