                                    await lblVehiclePowertrain.DoThreadSafeAsync(x => x.Visible = false, token)
                                                              .ConfigureAwait(false);
                                    await lblVehicleCosmeticLabel.DoThreadSafeAsync(x => x.Visible = false, token)
