                                    x.Maximum = 0;
                                    x.Minimum = 0;
                                    x.Value = 0;
                                    x.Visible = false;
                                }, token).ConfigureAwait(false);
                                await lblVehicleRatingLabel.DoThreadSafeAsync(x => x.Visible = false, token)
