                                    x.Enabled = chkVehicleActiveCommlink.Visible
                                                && objCyberware.GetTotalMatrixAttribute("Program Limit")
                                                >= (CharacterObject.DEP.TotalValue > intDeviceRating ? 2 : 1);
                                }, token).ConfigureAwait(false);
