                            string strSensor = (await objVehicle.GetCalculatedSensorAsync(token).ConfigureAwait(false)).ToString(GlobalSettings.CultureInfo);
                            await lblVehicleSensor.DoThreadSafeAsync(x => x.Text = strSensor, token)
