                                {
                                    blnFound = true;
                                }
                            }
                            if (!blnFound)
                            {
                                treGear.Nodes.Add(objNode);
                                _objCharacter.Locations.Add(objNode.Text);
                            }
                        }
                    }
                }
                foreach (Cyberware objPlugin in objCyberware.Children)
                {
                    if (objPlugin.AllowGear != null)
                    {
                        if (objPlugin.AllowGear["gearcategory"] != null)
