                treVehicles.SelectedNode.ForeColor = SystemColors.GrayText;
            else
                treVehicles.SelectedNode.ForeColor = SystemColors.WindowText;
            treVehicles.SelectedNode.ToolTipText = objAccessory.Notes.WordWrap(100);
        }

