                            intComplexForms++;
                        }
                        lblPBuildComplexForms.Text = String.Format("{0} " + LanguageManager.GetString("String_Of") + " {1}", (_objCharacter.CFPLimit - intComplexForms).ToString(), _objCharacter.CFPLimit.ToString());
                        */

                        ScheduleCharacterUpdate();

                        IsDirty = true;
                    }
                }
            }
        }

